 1→/**
 2→ * Post-processing sanitisation for AI-generated LaTeX content.
 3→ *
 4→ * Catches common AI mistakes that would break LaTeX compilation:
 5→ * - Markdown headings (# HEADING → \section{Heading})
 6→ * - Bare # characters outside comments
 7→ * - Markdown bold (**text** → \textbf{text})
 8→ * - Markdown bullet lists (- item → \item)
 9→ */
10→
11→/**
12→ * Sanitise AI-generated LaTeX, fixing markdown artefacts.
13→ * Applied after generation, before saving to DB.
14→ */
15→export function sanitiseLatexOutput(content: string): string {
16→  // Split into body and BibTeX trailer — only sanitise the body
17→  const bibtexMarker = "---BIBTEX---";
18→  const markerIdx = content.indexOf(bibtexMarker);
19→  let body = markerIdx >= 0 ? content.slice(0, markerIdx) : content;
20→  const trailer = markerIdx >= 0 ? content.slice(markerIdx) : "";
21→
22→  // 1. Convert markdown headings to LaTeX sections
23→  //    Must be done BEFORE escaping bare # (so we don't escape heading markers)
24→  body = body.replace(/^######\s+(.+)$/gm, (_m, title) => `\\subparagraph{${title.trim()}}`);
25→  body = body.replace(/^#####\s+(.+)$/gm, (_m, title) => `\\paragraph{${title.trim()}}`);
26→  body = body.replace(/^####\s+(.+)$/gm, (_m, title) => `\\subsubsubsection{${title.trim()}}`);
27→  body = body.replace(/^###\s+(.+)$/gm, (_m, title) => `\\subsubsection{${title.trim()}}`);
28→  body = body.replace(/^##\s+(.+)$/gm, (_m, title) => `\\subsection{${title.trim()}}`);
29→  body = body.replace(/^#\s+(.+)$/gm, (_m, title) => `\\section{${title.trim()}}`);
30→
31→  // 2. Convert markdown bold to LaTeX bold
32→  body = body.replace(/\*\*([^*]+)\*\*/g, "\\textbf{$1}");
33→
34→  // 3. Convert markdown italic to LaTeX italic
35→  body = body.replace(/(?<!\*)\*([^*]+)\*(?!\*)/g, "\\textit{$1}");
36→
37→  // 4. Escape bare # that are NOT in LaTeX commands and NOT in comments
38→  //    A bare # is one not preceded by a backslash and not at line start as a heading (already converted)
39→  body = body.replace(/^(%.*$)/gm, "%%COMMENT%%$1"); // Temporarily protect comments
40→  body = body.replace(/(?<!\\)#/g, "\\#");
41→  body = body.replace(/%%COMMENT%%(%.*$)/gm, "$1"); // Restore comments
42→
43→  return trailer ? `${body}${trailer}` : body;
44→}
