            </p>
            <p className="mt-1 text-xs text-muted-foreground/70">
              DOCX, TXT, or PDF up to {Math.round(maxSize / (1024 * 1024))}MB
            </p>
          </>
        )}

        {state === "uploading" && (
          <div className="w-full max-w-xs text-center">
            <p className="mb-2 text-sm text-primary">
              {fileName ? `Processing ${fileName}...` : "Processing..."}
            </p>
            <div className="h-2 w-full overflow-hidden rounded-full bg-muted">
              <div
                className="h-full rounded-full bg-primary transition-all duration-300"
                style={{ width: `${progress}%` }}
              />
            </div>
            <p className="mt-1 text-xs text-muted-foreground">{progress}%</p>
          </div>
        )}

        {state === "complete" && (
          <div className="text-center">
            <svg
              className="mx-auto mb-1 h-8 w-8 text-green-500"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
              strokeWidth={2}
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                d="M9 12.75 11.25 15 15 9.75M21 12a9 9 0 1 1-18 0 9 9 0 0 1 18 0Z"
              />
            </svg>
            <p className="text-sm font-medium text-green-500">
              {fileName ?? "File"} uploaded successfully
            </p>
            <button
              type="button"
              onClick={(e) => {
                e.stopPropagation();
                resetState();
              }}
              className="mt-2 text-xs text-green-500/80 underline hover:text-green-500"
            >
              Upload another file
            </button>
          </div>
        )}

        {state === "error" && (
          <div className="text-center">
            <svg
              className="mx-auto mb-1 h-8 w-8 text-destructive"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
              strokeWidth={2}
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                d="M12 9v3.75m9-.75a9 9 0 1 1-18 0 9 9 0 0 1 18 0Zm-9 3.75h.008v.008H12v-.008Z"
              />
            </svg>
            <p className="text-sm text-destructive">
              {errorMessage ?? "An error occurred"}
            </p>
            <button
              type="button"
              onClick={(e) => {
                e.stopPropagation();
                resetState();
              }}
              className="mt-2 text-xs text-destructive/80 underline hover:text-destructive"
            >
              Try again
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

