  1→"use client";
  2→
  3→import { useState, useCallback } from "react";
  4→import { toast } from "sonner";
  5→import type { Project, UniversityType, ProjectMetadata } from "@/lib/types/database";
  6→import type { ParsedSynopsis } from "@/lib/synopsis/parser";
  7→import { UniversityStep } from "@/components/wizard/steps/university-step";
  8→import { SynopsisUploadStep } from "@/components/wizard/steps/synopsis-upload-step";
  9→import { ParsedDataReviewStep } from "@/components/wizard/steps/parsed-data-review-step";
 10→import { MetadataFormStep } from "@/components/wizard/steps/metadata-form-step";
 11→import { TitlePagePreviewStep } from "@/components/wizard/steps/title-page-preview-step";
 12→import { cn } from "@/lib/utils";
 13→
 14→const STEPS = [
 15→  { number: 1, label: "University" },
 16→  { number: 2, label: "Synopsis" },
 17→  { number: 3, label: "Review" },
 18→  { number: 4, label: "Metadata" },
 19→  { number: 5, label: "Preview" },
 20→] as const;
 21→
 22→interface SetupWizardProps {
 23→  project: Project;
 24→}
 25→
 26→export function SetupWizard({ project }: SetupWizardProps) {
 27→  const [currentStep, setCurrentStep] = useState(1);
 28→  const [saving, setSaving] = useState(false);
 29→
 30→  // Step 1 state
 31→  const [universityType, setUniversityType] = useState<UniversityType | null>(
 32→    project.university_type
 33→  );
 34→
 35→  // Step 2 state
 36→  const [synopsisText, setSynopsisText] = useState<string | null>(
 37→    project.synopsis_text
 38→  );
 39→  const [aiConsentAccepted, setAiConsentAccepted] = useState(false);
 40→
 41→  // Step 3 state
 42→  const [parsedData, setParsedData] = useState<ParsedSynopsis | null>(null);
 43→
 44→  // Step 4 state
 45→  const [metadata, setMetadata] = useState<ProjectMetadata>(
 46→    project.metadata_json ?? {}
 47→  );
 48→
 49→  // Derived
 50→  const isSandbox = project.status === "sandbox";
 51→
 52→  const patchProject = useCallback(
 53→    async (body: Record<string, unknown>) => {
 54→      setSaving(true);
 55→      try {
 56→        const res = await fetch(`/api/projects/${project.id}`, {
 57→          method: "PATCH",
 58→          headers: { "Content-Type": "application/json" },
 59→          body: JSON.stringify(body),
 60→        });
 61→
 62→        if (!res.ok) {
 63→          const err = await res.json().catch(() => null);
 64→          throw new Error(
 65→            err?.error?.message ?? `Failed to save (${res.status})`
 66→          );
 67→        }
 68→
 69→        return await res.json();
 70→      } catch (error) {
 71→        const message =
 72→          error instanceof Error ? error.message : "An unexpected error occurred";
 73→        toast.error(message);
 74→        throw error;
 75→      } finally {
 76→        setSaving(false);
 77→      }
 78→    },
 79→    [project.id]
 80→  );
 81→
 82→  const handleNext = useCallback(async () => {
 83→    try {
 84→      if (currentStep === 1) {
 85→        if (!universityType) {
 86→          toast.error("Please select a university type to continue.");
 87→          return;
 88→        }
 89→        await patchProject({ university_type: universityType });
 90→      }
 91→
 92→      if (currentStep === 2) {
 93→        if (!aiConsentAccepted) {
 94→          toast.error("Please accept the AI processing consent to continue.");
 95→          return;
 96→        }
 97→        if (!synopsisText || synopsisText.trim().length < 20) {
 98→          toast.error("Please provide your synopsis text (at least 20 characters) to continue.");
 99→          return;
100→        }
101→        await patchProject({ synopsis_text: synopsisText });
102→      }
103→
104→      if (currentStep === 3) {
105→        if (!parsedData) {
106→          toast.error("Please wait for synopsis parsing to complete before continuing.");
107→          return;
108→        }
109→        if (parsedData) {
110→          await patchProject({
111→            title: parsedData.title ?? project.title,
112→            study_type: parsedData.study_type ?? undefined,
113→          });
114→
115→          // Auto-fill metadata from parsed synopsis (only fill empty fields)
116→          const seeded = { ...metadata };
117→          if (!seeded.candidate_name && parsedData.candidate_name) seeded.candidate_name = parsedData.candidate_name;
118→          if (!seeded.registration_no && parsedData.registration_no) seeded.registration_no = parsedData.registration_no;
119→          if (!seeded.guide_name && parsedData.guide_name) seeded.guide_name = parsedData.guide_name;
120→          if (!seeded.co_guide_name && parsedData.co_guide_name) seeded.co_guide_name = parsedData.co_guide_name;
121→          if (!seeded.department && parsedData.department) seeded.department = parsedData.department;
122→          if (!seeded.institute_name && parsedData.institute_name) seeded.institute_name = parsedData.institute_name;
123→          setMetadata(seeded);
124→        }
125→      }
126→
127→      if (currentStep === 4) {
128→        await patchProject({ metadata_json: metadata });
129→      }
130→
131→      setCurrentStep((prev) => Math.min(prev + 1, 5));
132→    } catch {
133→      // Error already shown via toast in patchProject
134→    }
135→  }, [
136→    currentStep,
137→    universityType,
138→    synopsisText,
139→    aiConsentAccepted,
140→    parsedData,
141→    metadata,
142→    patchProject,
143→    project.title,
144→  ]);
145→
146→  const handleBack = useCallback(() => {
147→    setCurrentStep((prev) => Math.max(prev - 1, 1));
148→  }, []);
149→
150→  return (
151→    <div className="mx-auto max-w-3xl">
152→      {/* Stepper */}
153→      <nav aria-label="Setup progress" className="mb-8">
154→        <ol className="flex items-center justify-between">
155→          {STEPS.map((step, index) => {
156→            const isActive = step.number === currentStep;
157→            const isCompleted = step.number < currentStep;
158→            const isPending = step.number > currentStep;
159→
160→            return (
161→              <li key={step.number} className="flex flex-1 items-center">
162→                <div className="flex flex-col items-center gap-1.5">
163→                  <div
164→                    className={cn(
165→                      "flex h-9 w-9 items-center justify-center rounded-full border-2 text-sm font-semibold transition-colors",
166→                      isActive &&
167→                        "border-blue-600 bg-blue-600 text-white",
168→                      isCompleted &&
169→                        "border-green-600 bg-green-600 text-white",
170→                      isPending &&
171→                        "border-gray-300 bg-white text-gray-400"
172→                    )}
173→                  >
174→                    {isCompleted ? (
175→                      <svg
176→                        className="h-4 w-4"
177→                        fill="none"
178→                        viewBox="0 0 24 24"
179→                        stroke="currentColor"
180→                        strokeWidth={3}
181→                      >
182→                        <path
183→                          strokeLinecap="round"
184→                          strokeLinejoin="round"
185→                          d="M5 13l4 4L19 7"
186→                        />
187→                      </svg>
188→                    ) : (
189→                      step.number
190→                    )}
191→                  </div>
192→                  <span
193→                    className={cn(
194→                      "text-xs font-medium",
195→                      isActive && "text-blue-600",
196→                      isCompleted && "text-green-600",
197→                      isPending && "text-gray-400"
198→                    )}
199→                  >
200→                    {step.label}
201→                  </span>
202→                </div>
203→
204→                {/* Connector line */}
205→                {index < STEPS.length - 1 && (
206→                  <div
207→                    className={cn(
208→                      "mx-2 h-0.5 flex-1 transition-colors",
209→                      step.number < currentStep
210→                        ? "bg-green-600"
211→                        : "bg-gray-200"
212→                    )}
213→                  />
214→                )}
215→              </li>
216→            );
217→          })}
218→        </ol>
219→      </nav>
220→
221→      {/* Step content */}
222→      <div className="min-h-[400px] rounded-lg border border-gray-200 bg-white p-6 shadow-sm">
223→        {currentStep === 1 && (
224→          <UniversityStep value={universityType} onChange={setUniversityType} />
225→        )}
226→
227→        {currentStep === 2 && (
228→          <SynopsisUploadStep
229→            projectId={project.id}
230→            synopsisText={synopsisText}
231→            onSynopsisChange={setSynopsisText}
232→            aiConsentAccepted={aiConsentAccepted}
233→            onAiConsentChange={setAiConsentAccepted}
234→          />
235→        )}
236→
237→        {currentStep === 3 && (
238→          <ParsedDataReviewStep
239→            synopsisText={synopsisText}
240→            parsedData={parsedData}
241→            onParsedDataChange={setParsedData}
242→          />
243→        )}
244→
245→        {currentStep === 4 && (
246→          <MetadataFormStep metadata={metadata} onChange={setMetadata} />
247→        )}
248→
249→        {currentStep === 5 && (
250→          <TitlePagePreviewStep
251→            project={{
252→              ...project,
253→              university_type: universityType,
254→              title: parsedData?.title ?? project.title,
255→              metadata_json: metadata,
256→            }}
257→            isSandbox={isSandbox}
258→          />
259→        )}
260→      </div>
261→
262→      {/* Navigation buttons */}
263→      <div className="mt-6 flex items-center justify-between">
264→        <button
265→          type="button"
266→          onClick={handleBack}
267→          disabled={currentStep === 1 || saving}
268→          className={cn(
269→            "rounded-md border border-gray-300 px-5 py-2.5 text-sm font-medium transition-colors",
270→            currentStep === 1
271→              ? "cursor-not-allowed opacity-0"
272→              : "hover:bg-gray-50"
273→          )}
274→        >
275→          Back
276→        </button>
277→
278→        {currentStep < 5 && (
279→          <button
280→            type="button"
281→            onClick={handleNext}
282→            disabled={
283→              saving ||
284→              (currentStep === 1 && !universityType) ||
285→              (currentStep === 2 && (!aiConsentAccepted || !synopsisText || synopsisText.trim().length < 20)) ||
286→              (currentStep === 3 && !parsedData)
287→            }
288→            className={cn(
289→              "rounded-md bg-blue-600 px-5 py-2.5 text-sm font-medium text-white transition-colors hover:bg-blue-700 disabled:cursor-not-allowed disabled:opacity-60"
290→            )}
291→          >
292→            {saving ? "Saving..." : "Next"}
293→          </button>
294→        )}
295→      </div>
296→    </div>
297→  );
298→}
