 1→import { NextRequest, NextResponse } from "next/server";
 2→import { getAuthenticatedUser } from "@/lib/api/auth";
 3→import {
 4→  unauthorised,
 5→  validationError,
 6→  internalError,
 7→} from "@/lib/api/errors";
 8→import { createServerSupabaseClient } from "@/lib/supabase/server";
 9→import { projectCreateSchema } from "@/lib/validation/schemas";
10→
11→export async function GET() {
12→  try {
13→    const authResult = await getAuthenticatedUser();
14→    if (!authResult) return unauthorised();
15→
16→    const supabase = await createServerSupabaseClient();
17→
18→    const { data: projects, error } = await supabase
19→      .from("projects")
20→      .select("*")
21→      .eq("user_id", authResult.user.id)
22→      .order("updated_at", { ascending: false });
23→
24→    if (error) {
25→      console.error("Failed to fetch projects:", error);
26→      return internalError("Failed to fetch projects");
27→    }
28→
29→    return NextResponse.json({ data: projects });
30→  } catch (err) {
31→    console.error("Unexpected error in GET /api/projects:", err);
32→    return internalError();
33→  }
34→}
35→
36→export async function POST(request: NextRequest) {
37→  try {
38→    const authResult = await getAuthenticatedUser();
39→    if (!authResult) return unauthorised();
40→
41→    const body = await request.json();
42→    const parsed = projectCreateSchema.safeParse(body);
43→
44→    if (!parsed.success) {
45→      return validationError("Invalid project data", {
46→        issues: parsed.error.flatten().fieldErrors,
47→      });
48→    }
49→
50→    const { user } = authResult;
51→    const supabase = await createServerSupabaseClient();
52→
53→    // Sandbox project limit: max 1 sandbox project per user
54→    const { count: sandboxCount } = await supabase
55→      .from("projects")
56→      .select("id", { count: "exact", head: true })
57→      .eq("user_id", user.id)
58→      .eq("status", "sandbox");
59→
60→    if ((sandboxCount ?? 0) >= 1) {
61→      return NextResponse.json(
62→        {
63→          error: {
64→            code: "SANDBOX_LIMIT",
65→            message:
66→              "You already have a sandbox project. Purchase a licence to unlock it, or delete it to start a new one.",
67→            action: "purchase_licence",
68→          },
69→        },
70→        { status: 402 }
71→      );
72→    }
73→
74→    const { data: project, error } = await supabase
75→      .from("projects")
76→      .insert({
77→        user_id: user.id,
78→        title: parsed.data.title,
79→        university_type: parsed.data.university_type,
80→        status: "sandbox",
81→        current_phase: 0,
82→        metadata_json: {},
83→        phases_completed: [],
84→      })
85→      .select("*")
86→      .single();
87→
88→    if (error) {
89→      console.error("Failed to create project:", error);
90→      return internalError("Failed to create project");
91→    }
92→
93→    return NextResponse.json({ data: project }, { status: 201 });
94→  } catch (err) {
95→    console.error("Unexpected error in POST /api/projects:", err);
96→    return internalError();
97→  }
98→}
