  1→import { NextRequest, NextResponse } from "next/server";
  2→import { getAuthenticatedUser } from "@/lib/api/auth";
  3→import {
  4→  unauthorised,
  5→  notFound,
  6→  validationError,
  7→  internalError,
  8→} from "@/lib/api/errors";
  9→import { createServerSupabaseClient } from "@/lib/supabase/server";
 10→import { createAdminSupabaseClient } from "@/lib/supabase/admin";
 11→import { projectUpdateSchema } from "@/lib/validation/schemas";
 12→import { deleteR2Prefix } from "@/lib/r2/client";
 13→
 14→export async function GET(
 15→  _request: NextRequest,
 16→  { params }: { params: Promise<{ id: string }> }
 17→) {
 18→  try {
 19→    const authResult = await getAuthenticatedUser();
 20→    if (!authResult) return unauthorised();
 21→
 22→    const { id } = await params;
 23→    const supabase = await createServerSupabaseClient();
 24→
 25→    const { data: project, error } = await supabase
 26→      .from("projects")
 27→      .select("*")
 28→      .eq("id", id)
 29→      .single();
 30→
 31→    if (error || !project) {
 32→      return notFound("Project not found");
 33→    }
 34→
 35→    return NextResponse.json({ data: project });
 36→  } catch (err) {
 37→    console.error("Unexpected error in GET /api/projects/[id]:", err);
 38→    return internalError();
 39→  }
 40→}
 41→
 42→export async function PATCH(
 43→  request: NextRequest,
 44→  { params }: { params: Promise<{ id: string }> }
 45→) {
 46→  try {
 47→    const authResult = await getAuthenticatedUser();
 48→    if (!authResult) return unauthorised();
 49→
 50→    const { id } = await params;
 51→    const body = await request.json();
 52→    const parsed = projectUpdateSchema.safeParse(body);
 53→
 54→    if (!parsed.success) {
 55→      return validationError("Invalid update data", {
 56→        issues: parsed.error.flatten().fieldErrors,
 57→      });
 58→    }
 59→
 60→    const supabase = await createServerSupabaseClient();
 61→
 62→    const { data: project, error } = await supabase
 63→      .from("projects")
 64→      .update(parsed.data)
 65→      .eq("id", id)
 66→      .select("*")
 67→      .single();
 68→
 69→    if (error || !project) {
 70→      return notFound("Project not found");
 71→    }
 72→
 73→    // Record AI consent timestamp on first synopsis upload (9.3)
 74→    if (
 75→      parsed.data.synopsis_text &&
 76→      !authResult.user.ai_consent_accepted_at
 77→    ) {
 78→      const adminDb = createAdminSupabaseClient();
 79→      await adminDb
 80→        .from("users")
 81→        .update({ ai_consent_accepted_at: new Date().toISOString() })
 82→        .eq("id", authResult.user.id)
 83→        .is("ai_consent_accepted_at", null);
 84→    }
 85→
 86→    return NextResponse.json({ data: project });
 87→  } catch (err) {
 88→    console.error("Unexpected error in PATCH /api/projects/[id]:", err);
 89→    return internalError();
 90→  }
 91→}
 92→
 93→export async function DELETE(
 94→  _request: NextRequest,
 95→  { params }: { params: Promise<{ id: string }> }
 96→) {
 97→  try {
 98→    const authResult = await getAuthenticatedUser();
 99→    if (!authResult) return unauthorised();
100→
101→    const { id } = await params;
102→    const supabase = await createServerSupabaseClient();
103→
104→    // Verify project exists and belongs to user (RLS handles ownership)
105→    const { data: project, error: fetchError } = await supabase
106→      .from("projects")
107→      .select("id")
108→      .eq("id", id)
109→      .single();
110→
111→    if (fetchError || !project) {
112→      return notFound("Project not found");
113→    }
114→
115→    // Clean R2 objects for this project (9.9)
116→    try {
117→      await deleteR2Prefix(`projects/${id}/`);
118→    } catch (r2Err) {
119→      console.error(`R2 cleanup failed for project ${id}:`, r2Err);
120→      // Continue with DB deletion even if R2 cleanup fails
121→    }
122→
123→    // Hard-delete project (CASCADE handles child tables + audit_log)
124→    const { error: deleteError } = await supabase
125→      .from("projects")
126→      .delete()
127→      .eq("id", id);
128→
129→    if (deleteError) {
130→      console.error("Failed to delete project:", deleteError);
131→      return internalError("Failed to delete project");
132→    }
133→
134→    return NextResponse.json({ data: { deleted: true } });
135→  } catch (err) {
136→    console.error("Unexpected error in DELETE /api/projects/[id]:", err);
137→    return internalError();
138→  }
139→}
