54→          type="button"
55→          onClick={(e) => {
56→            e.preventDefault();
57→            setConfirming(false);
58→          }}
59→          className="rounded px-2 py-0.5 text-xs font-medium bg-muted text-muted-foreground hover:bg-muted/80"
60→        >
61→          No
62→        </button>
63→      </div>
64→    );
65→  }
66→
67→  return (
68→    <button
69→      type="button"
70→      onClick={(e) => {
71→        e.preventDefault();
72→        setConfirming(true);
73→      }}
