DROP POLICY IF EXISTS "Users can delete own projects" ON public.projects;
CREATE POLICY "Users can delete own projects"
  ON public.projects FOR DELETE TO authenticated
  USING (user_id = public.get_current_user_id());

DROP POLICY IF EXISTS "Admins can view org projects" ON public.projects;

input.old_string:
"use client";

import { useState } from "react";
import { Trash2 } from "lucide-react";
import { useRouter } from "next/navigation";

interface DeleteProjectButtonProps {
  projectId: string;
  projectTitle: string;
}

export function DeleteProjectButton({
  projectId,
  projectTitle,
}: DeleteProjectButtonProps) {
  const [confirming, setConfirming] = useState(false);
  const [deleting, setDeleting] = useState(false);
  const router = useRouter();

  async function handleDelete() {
    setDeleting(true);
    try {
      const resp = await fetch(`/api/projects/${projectId}`, {
        method: "DELETE",
      });
      if (resp.ok) {
        router.refresh();
      }
    } finally {
      setDeleting(false);
      setConfirming(false);
    }
  }

  if (confirming) {
    return (
      <div
        className="flex items-center gap-2"
        onClick={(e) => e.preventDefault()}
      >
        <span className="text-xs text-destructive">Delete?</span>
        <button
          type="button"
          onClick={(e) => {
            e.preventDefault();
            void handleDelete();
          }}
          disabled={deleting}
          className="rounded px-2 py-0.5 text-xs font-medium bg-destructive text-destructive-foreground hover:bg-destructive/90 disabled:opacity-50"
        >
          {deleting ? "..." : "Yes"}
        </button>
        <button
          type="button"
          onClick={(e) => {
            e.preventDefault();
            setConfirming(false);
          }}
          className="rounded px-2 py-0.5 text-xs font-medium bg-muted text-muted-foreground hover:bg-muted/80"
        >
          No
        </button>
      </div>
    );
  }

  return (
    <button
      type="button"
      onClick={(e) => {
        e.preventDefault();
        setConfirming(true);
      }}
      className="rounded p-1 text-muted-foreground/50 hover:text-destructive hover:bg-destructive/10 transition-colors"
      title={`Delete ${projectTitle}`}
    >
      <Trash2 className="h-4 w-4" />
    </button>
  );
}

