"use client";

import { useState, useEffect } from "react";
import Link from "next/link";
import { ArrowRight } from "lucide-react";
import { motion, useReducedMotion } from "motion/react";

// ── TypewriterText ──────────────────────────────────────────────────────────
function TypewriterText({
  text,
  charDelay = 50,
  startDelay = 1200,
  reducedMotion = false,
}: {
  text: string;
  charDelay?: number;
  startDelay?: number;
  reducedMotion?: boolean;
}) {
  const [displayed, setDisplayed] = useState(reducedMotion ? text : "");
  const [showCursor, setShowCursor] = useState(!reducedMotion);

  useEffect(() => {
    if (reducedMotion) return;
    const startTimeout = setTimeout(() => {
      let i = 0;
      const interval = setInterval(() => {
        i++;
        setDisplayed(text.slice(0, i));
        if (i >= text.length) {
          clearInterval(interval);
          setTimeout(() => setShowCursor(false), 2000);
        }
      }, charDelay);
      return () => clearInterval(interval);
    }, startDelay);
    return () => clearTimeout(startTimeout);
  }, [text, charDelay, startDelay, reducedMotion]);

  return (
    <span>
      {displayed}
      {showCursor && (
        <span className="animate-typewriter-cursor">|</span>
      )}
    </span>
  );
}

// ── ParagraphBlock — simulates a real paragraph of text lines ────────────
function ParagraphBlock({ widths }: { widths: string[] }) {
  return (
    <div className="space-y-[4px]">
      {widths.map((w, i) => (
        <div
          key={i}
          className="h-[1.5px] rounded-full bg-[#C8C8C8]"
          style={{ width: w }}
        />
      ))}
    </div>
  );
}

// ── ThesisPage — tall portrait page with paper curl effects ─────────────
function ThesisPage({
  curlVariant,
  paragraphs,
  titleWidth,
}: {
  curlVariant: "corner" | "bottom" | "side";
  paragraphs: string[][];
  titleWidth: string;
}) {
  const curlClass =
    curlVariant === "corner"
      ? "paper-curl-corner"
      : curlVariant === "bottom"
        ? "paper-curl-bottom"
        : "paper-curl-side";

  return (
    <div className={`rounded-[3px] bg-white px-6 py-5 ${curlClass}`}>
      {/* Title line */}
      <div
        className="mb-3 h-[2.5px] rounded-full bg-[#B0B0B0]"
        style={{ width: titleWidth }}
      />
      {/* Paragraph blocks */}
      <div className="flex flex-col gap-[12px]">
        {paragraphs.map((widths, i) => (
          <ParagraphBlock key={i} widths={widths} />
        ))}
      </div>
    </div>
  );
}

// ── GlassSphere — 3D glass with radial gradient specular highlight ──────
function GlassSphere({
  size,
  baseColor,
  darkColor,
  top,
  left,
  delay,
  opacity = 1,
}: {
  size: number;
  baseColor: string;
  darkColor: string;
  top: number;
  left: number;
  delay: string;
  opacity?: number;
}) {
  return (
    <div
      className="absolute rounded-full border border-white/25 animate-float-sphere"
      style={{
        width: size,
        height: size,
        top,
        left,
        animationDelay: delay,
        opacity,
        background: `radial-gradient(circle at 30% 25%,
          rgba(255,255,255,0.7) 0%,
          rgba(255,255,255,0.15) 28%,
          rgba(${baseColor}, 0.85) 60%,
          rgba(${darkColor}, 0.95) 100%)`,
        boxShadow: `inset 0 -4px 12px rgba(0,0,0,0.08),
                     0 4px 16px -4px rgba(0,0,0,0.15)`,
      }}
    />
  );
}

// ── Page data ───────────────────────────────────────────────────────────
const PAGE_A_PARAGRAPHS = [
  ["88%", "100%", "72%", "95%"],
  ["60%", "100%", "78%", "52%"],
  ["90%", "68%", "100%", "55%"],
];

const PAGE_B_PARAGRAPHS = [
  ["82%", "100%", "66%", "95%", "55%"],
  ["90%", "74%", "100%", "48%", "85%"],
  ["100%", "60%", "92%", "70%", "44%"],
];

const PAGE_C_PARAGRAPHS = [
  ["92%", "100%", "70%", "88%"],
  ["58%", "100%", "75%", "46%"],
];

// ── Sphere data ─────────────────────────────────────────────────────────
const SPHERES = [
  { size: 90, top: 0, left: 30, baseColor: "122,140,102", darkColor: "80,100,60", delay: "0s" },
  { size: 72, top: 55, left: 110, baseColor: "196,147,90", darkColor: "160,110,55", delay: "0.8s" },
  { size: 62, top: 100, left: 10, baseColor: "232,220,200", darkColor: "200,185,160", delay: "1.4s" },
  { size: 50, top: 25, left: 130, baseColor: "168,184,148", darkColor: "130,150,110", delay: "1.2s" },
  { size: 42, top: 135, left: 90, baseColor: "140,160,120", darkColor: "100,120,80", delay: "0.6s" },
  { size: 30, top: 155, left: 45, baseColor: "58,58,58", darkColor: "30,30,30", delay: "2.0s" },
  { size: 26, top: 75, left: 0, baseColor: "196,147,90", darkColor: "160,110,55", delay: "2.4s" },
  { size: 34, top: 175, left: 120, baseColor: "232,220,200", darkColor: "200,185,160", delay: "1.6s" },
  { size: 20, top: 95, left: 160, baseColor: "122,140,102", darkColor: "80,100,60", delay: "1.8s" },
  { size: 16, top: 190, left: 70, baseColor: "240,230,214", darkColor: "210,200,180", delay: "0.4s" },
] as const;

// ── HeroSection ─────────────────────────────────────────────────────────
export function HeroSection() {
  const prefersReducedMotion = useReducedMotion();

  const dur = prefersReducedMotion ? 0 : 1.0;
  const zenEase = [0.16, 1, 0.3, 1] as const;

  return (
    <section className="relative min-h-[90vh] overflow-hidden pt-32">
      <div className="container">
        <div className="grid grid-cols-1 place-items-center gap-12 lg:grid-cols-12 lg:gap-8">
          {/* ── Left zone ──────────────────────────────────────────────── */}
          <div className="lg:col-span-5">
            <motion.h1
              className="font-serif text-[80px] font-normal leading-[0.95] tracking-[-0.03em] text-[#2F2F2F] md:text-[140px]"
              initial={{ opacity: 0, y: 60, scale: 0.95 }}
              animate={{ opacity: 1, y: 0, scale: 1 }}
              transition={{ duration: dur, delay: 0.2, ease: zenEase }}
            >
              Apollo
            </motion.h1>

            <motion.p
              className="mt-8 max-w-md font-typewriter text-[17px] leading-relaxed text-[#6B6B6B] md:text-[19px]"
              initial={{ opacity: 0, y: 40 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: prefersReducedMotion ? 0 : 0.8, delay: 0.4, ease: zenEase }}
            >
              <TypewriterText
                text="From synopsis to submission — structured, cited, compliant."
                reducedMotion={prefersReducedMotion ?? false}
              />
            </motion.p>

            <motion.div
              className="mt-10 flex items-center gap-4"
              initial={{ opacity: 0, y: 40 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: prefersReducedMotion ? 0 : 0.8, delay: 0.6, ease: zenEase }}
            >
              <Link
                href="/sign-up"
                className="inline-flex items-center gap-2 rounded-full bg-[#2F2F2F] px-6 py-3 text-[15px] font-medium text-white transition-colors hover:bg-[#2F2F2F]/90"
              >
                Get Started Free
                <ArrowRight className="h-4 w-4" />
              </Link>
              <a
                href="#features"
                className="inline-flex items-center rounded-full border border-[rgba(0,0,0,0.06)] px-6 py-3 text-[15px] font-medium text-[#2F2F2F] transition-colors hover:bg-black/[0.04]"
              >
                Learn More
              </a>
            </motion.div>
          </div>

          {/* ── Right zone — papers + spheres composition ───────────── */}
          <div
            className="relative hidden h-[560px] w-full lg:col-span-7 lg:block"
            style={{ perspective: "1200px" }}
          >
            {/* ── Page A (back-left, corner curl) ────────────────────── */}
            <motion.div
              className="absolute paper-page animate-float-thesis"
              style={{
                left: "4%",
                top: "10%",
                width: 230,
                transform: "rotate(-15deg)",
                animationDelay: "0s",
                zIndex: 1,
              }}
              initial={{ opacity: 0, y: 40, scale: 0.88 }}
              animate={{ opacity: 1, y: 0, scale: 1 }}
              transition={{ duration: dur, delay: 0.5, ease: zenEase }}
            >
              <ThesisPage
                curlVariant="corner"
                titleWidth="50%"
                paragraphs={PAGE_A_PARAGRAPHS}
              />
            </motion.div>

            {/* ── Page B (centre-front, bottom roll, on top) ─────────── */}
            <motion.div
              className="absolute paper-page animate-float-thesis"
              style={{
                left: "16%",
                top: "0%",
                width: 250,
                transform: "rotate(-6deg) rotateX(2deg)",
                animationDelay: "1.5s",
                zIndex: 3,
              }}
              initial={{ opacity: 0, y: 40, scale: 0.88 }}
              animate={{ opacity: 1, y: 0, scale: 1 }}
              transition={{ duration: dur, delay: 0.65, ease: zenEase }}
            >
              <ThesisPage
                curlVariant="bottom"
                titleWidth="45%"
                paragraphs={PAGE_B_PARAGRAPHS}
              />
            </motion.div>

            {/* ── Page C (right-lean, side lift) ─────────────────────── */}
            <motion.div
              className="absolute paper-page animate-float-thesis"
              style={{
                left: "32%",
                top: "16%",
                width: 210,
                transform: "rotate(5deg) rotateY(-3deg)",
                animationDelay: "3s",
                zIndex: 2,
              }}
              initial={{ opacity: 0, y: 40, scale: 0.88 }}
              animate={{ opacity: 1, y: 0, scale: 1 }}
              transition={{ duration: dur, delay: 0.8, ease: zenEase }}
            >
              <ThesisPage
                curlVariant="side"
                titleWidth="55%"
                paragraphs={PAGE_C_PARAGRAPHS}
              />
            </motion.div>

            {/* ── Glass Spheres — tight cluster on the right ─────────── */}
            <motion.div
              className="absolute"
              style={{ right: "-2%", top: "3%", zIndex: 4 }}
              initial={{ opacity: 0, scale: 0.7 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ duration: dur, delay: 0.9, ease: zenEase }}
            >
              <div className="relative h-[240px] w-[200px]">
                {SPHERES.map((s, i) => (
                  <GlassSphere
                    key={i}
                    size={s.size}
                    top={s.top}
                    left={s.left}
                    baseColor={s.baseColor}
                    darkColor={s.darkColor}
                    delay={s.delay}
                  />
                ))}
              </div>
            </motion.div>
          </div>
        </div>
      </div>
    </section>
  );
}

