file.content:
@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  :root {
    --background: 40 7% 98%;
    --foreground: 0 0% 18%;
    --card: 0 0% 100%;
    --card-foreground: 0 0% 18%;
    --popover: 0 0% 100%;
    --popover-foreground: 0 0% 18%;
    --primary: 90 16% 54%;
    --primary-foreground: 0 0% 100%;
    --secondary: 27 42% 64%;
    --secondary-foreground: 0 0% 18%;
    --muted: 40 7% 96%;
    --muted-foreground: 0 0% 42%;
    --accent: 40 7% 96%;
    --accent-foreground: 0 0% 18%;
    --destructive: 0 84% 60%;
    --destructive-foreground: 0 0% 98%;
    --border: 0 0% 0% / 0.06;
    --input: 0 0% 0% / 0.06;
    --ring: 0 0% 18% / 0.1;
    --radius: 1rem;
    --sidebar-background: 0 0% 100% / 0.7;
    --sidebar-foreground: 0 0% 18%;
    --sidebar-primary: 90 16% 54%;
    --sidebar-primary-foreground: 0 0% 100%;
    --sidebar-accent: 40 7% 96%;
    --sidebar-accent-foreground: 0 0% 18%;
    --sidebar-border: 0 0% 0% / 0.06;
    --sidebar-ring: 0 0% 18% / 0.1;
  }

  .dark {
    --background: 240 6% 6%;
    --foreground: 0 0% 95%;
    --card: 240 5% 13%;
    --card-foreground: 0 0% 95%;
    --popover: 240 5% 13%;
    --popover-foreground: 0 0% 95%;
    --primary: 90 16% 54%;
    --primary-foreground: 0 0% 100%;
    --secondary: 240 4% 18%;
    --secondary-foreground: 0 0% 95%;
    --muted: 240 4% 18%;
    --muted-foreground: 240 5% 65%;
    --accent: 240 4% 18%;
    --accent-foreground: 0 0% 95%;
    --destructive: 0 62.8% 30.6%;
    --destructive-foreground: 0 0% 95%;
    --border: 240 4% 22%;
    --input: 240 4% 22%;
    --ring: 90 16% 54%;
    --sidebar-background: 240 5% 8%;
    --sidebar-foreground: 240 4.8% 95.9%;
    --sidebar-primary: 90 16% 54%;
    --sidebar-primary-foreground: 0 0% 100%;
    --sidebar-accent: 240 3.7% 15.9%;
    --sidebar-accent-foreground: 240 4.8% 95.9%;
    --sidebar-border: 240 3.7% 20%;
    --sidebar-ring: 90 16% 54%;
  }
}

@layer base {
  * {
    @apply border-border;
  }
  body {
    @apply bg-background text-foreground;
  }
}

/* Zen utility classes */
.glass {
  background: rgba(255, 255, 255, 0.7);
  backdrop-filter: blur(20px);
  -webkit-backdrop-filter: blur(20px);
}

.glass-nav {
  background: rgba(255, 255, 255, 0.45);
  backdrop-filter: blur(24px) saturate(180%);
  -webkit-backdrop-filter: blur(24px) saturate(180%);
}

.glass-nav-scrolled {
  background: rgba(255, 255, 255, 0.65);
  backdrop-filter: blur(24px) saturate(180%);
  -webkit-backdrop-filter: blur(24px) saturate(180%);
  box-shadow: 0 4px 30px rgba(0, 0, 0, 0.06);
}

.zen-shadow {
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.03);
}

.zen-shadow-hover {
  box-shadow: 0 8px 30px rgba(0, 0, 0, 0.06);
}

.zen-shadow-card {
  box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.05),
    0 10px 25px -3px rgba(0, 0, 0, 0.06),
    0 0 0 1px rgba(0, 0, 0, 0.02);
}

.zen-shadow-card-hover {
  box-shadow: 0 8px 12px -2px rgba(0, 0, 0, 0.07),
    0 20px 40px -4px rgba(0, 0, 0, 0.08),
    0 0 0 1px rgba(0, 0, 0, 0.03);
}

.paper-shadow {
  box-shadow:
    8px 20px 40px -10px rgba(0, 0, 0, 0.18),
    3px 8px 16px -5px rgba(0, 0, 0, 0.10);
}

.paper-page {
  filter: drop-shadow(10px 25px 30px rgba(0, 0, 0, 0.22))
          drop-shadow(4px 10px 12px rgba(0, 0, 0, 0.10));
}

.paper-curl-corner {
  position: relative;
}
.paper-curl-corner::after {
  content: '';
  position: absolute;
  bottom: 0;
  right: 0;
  width: 45px;
  height: 45px;
  background: linear-gradient(225deg, #E0E0E0 0%, #EEEEEE 40%, white 50%, transparent 50%);
  border-radius: 0 0 3px 0;
  box-shadow: -3px -3px 8px rgba(0, 0, 0, 0.06);
}

.paper-curl-bottom {
  position: relative;
}
.paper-curl-bottom::after {
  content: '';
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  height: 8px;
  background: linear-gradient(to bottom, transparent, rgba(0, 0, 0, 0.04));
  border-radius: 0 0 3px 3px;
}

.paper-curl-side {
  position: relative;
}
.paper-curl-side::after {
  content: '';
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  width: 8px;
  background: linear-gradient(to right, transparent, rgba(0, 0, 0, 0.05));
  border-radius: 0 3px 3px 0;
}

/* Floating animations */
@keyframes float-thesis {
  0%,
  100% {
    transform: translateY(0);
  }
  50% {
    transform: translateY(-14px);
  }
}

@keyframes float-sphere {
  0%,
  100% {
    transform: translateY(0);
  }
  50% {
    transform: translateY(-12px);
  }
}


@keyframes shimmer {
  0% {
    transform: translateX(-100%);
  }
  100% {
    transform: translateX(200%);
  }
}

/* Zen focus rings */
*:focus-visible {
  outline: 2px solid rgba(47, 47, 47, 0.1);
  outline-offset: 2px;
}

