// ── Sphere data ─────────────────────────────────────────────────────────
const SPHERES = [
  { size: 90, top: 0, left: 30, baseColor: "122,140,102", darkColor: "80,100,60", delay: "0s" },
  { size: 72, top: 55, left: 110, baseColor: "196,147,90", darkColor: "160,110,55", delay: "0.8s" },
