  8→// ── TypewriterText ──────────────────────────────────────────────────────────
  9→function TypewriterText({
 10→  text,
 11→  charDelay = 50,
 12→  startDelay = 1200,
 13→  reducedMotion = false,
 14→}: {
 15→  text: string;
 16→  charDelay?: number;
 17→  startDelay?: number;
 18→  reducedMotion?: boolean;
 19→}) {
 20→  const [displayed, setDisplayed] = useState(reducedMotion ? text : "");
 21→  const [showCursor, setShowCursor] = useState(!reducedMotion);
 22→
 23→  useEffect(() => {
 24→    if (reducedMotion) return;
 25→    const startTimeout = setTimeout(() => {
 26→      let i = 0;
 27→      const interval = setInterval(() => {
 28→        i++;
 29→        setDisplayed(text.slice(0, i));
 30→        if (i >= text.length) {
 31→          clearInterval(interval);
 32→          setTimeout(() => setShowCursor(false), 2000);
 33→        }
 34→      }, charDelay);
 35→      return () => clearInterval(interval);
 36→    }, startDelay);
 37→    return () => clearTimeout(startTimeout);
 38→  }, [text, charDelay, startDelay, reducedMotion]);
 39→
 40→  return (
 41→    <span>
 42→      {displayed}
 43→      {showCursor && (
 44→        <span className="animate-typewriter-cursor">|</span>
 45→      )}
 46→    </span>
 47→  );
 48→}
 49→
 50→// ── ParagraphBlock — simulates a real paragraph of text lines ────────────
 51→function ParagraphBlock({ widths }: { widths: string[] }) {
 52→  return (
