 14→}: {
 15→  text: string;
 16→  charDelay?: number;
 17→  startDelay?: number;
 18→  reducedMotion?: boolean;
 19→}) {
 20→  const [displayed, setDisplayed] = useState(reducedMotion ? text : "");
 21→  const [showCursor, setShowCursor] = useState(!reducedMotion);
 22→
 23→  useEffect(() => {
 24→    if (reducedMotion) return;
 25→    const startTimeout = setTimeout(() => {
 26→      let i = 0;
 27→      const interval = setInterval(() => {
 28→        i++;
 29→        setDisplayed(text.slice(0, i));
 30→        if (i >= text.length) {
 31→          clearInterval(interval);
 32→          setTimeout(() => setShowCursor(false), 2000);
 33→        }
 34→      }, charDelay);
 35→      return () => clearInterval(interval);
 36→    }, startDelay);
 37→    return () => clearTimeout(startTimeout);
 38→  }, [text, charDelay, startDelay, reducedMotion]);
 39→
 40→  return (
 41→    <span>
 42→      {displayed}
 43→      {showCursor && (
 44→        <span className="animate-typewriter-cursor">|</span>
 45→      )}
 46→    </span>
 47→  );
 48→}
 49→
 50→// ── ParagraphBlock — simulates a real paragraph of text lines ────────────
 51→function ParagraphBlock({ widths }: { widths: string[] }) {
 52→  return (
 53→    <div className="space-y-[4px]">
 54→      {widths.map((w, i) => (
 55→        <div
 56→          key={i}
 57→          className="h-[1.5px] rounded-full bg-[#C8C8C8]"
 58→          style={{ width: w }}
 59→        />
 60→      ))}
 61→    </div>
 62→  );
 63→}
 64→
 65→// ── ThesisPage — tall portrait page with paper curl effects ─────────────
 66→function ThesisPage({
 67→  curlVariant,
 68→  paragraphs,
 69→  titleWidth,
 70→}: {
 71→  curlVariant: "corner" | "bottom" | "side";
 72→  paragraphs: string[][];
 73→  titleWidth: string;
 74→}) {
 75→  const curlClass =
 76→    curlVariant === "corner"
 77→      ? "paper-curl-corner"
 78→      : curlVariant === "bottom"
 79→        ? "paper-curl-bottom"
 80→        : "paper-curl-side";
 81→
 82→  return (
 83→    <div className={`relative rounded-[3px] paper-surface px-6 py-5 ${curlClass} overflow-hidden`}>
 84→      {/* Light overlay — directional gradient simulating upper-left light */}
 85→      <div
 86→        className="pointer-events-none absolute inset-0 rounded-[3px]"
 87→        style={{
 88→          background:
 89→            "linear-gradient(135deg, rgba(255,255,255,0.35) 0%, rgba(255,255,255,0) 45%, rgba(0,0,0,0.015) 100%)",
 90→        }}
 91→      />
 92→      {/* Content */}
 93→      <div className="relative z-[1]">
 94→        <div
 95→          className="mb-3 h-[2.5px] rounded-full bg-[#B0B0B0]"
 96→          style={{ width: titleWidth }}
 97→        />
 98→        <div className="flex flex-col gap-[12px]">
