 43→    target: "[data-tour='editor']",
 44→    position: "right",
 45→  },
 46→  {
 47→    title: "You\u2019re Ready",
 48→    description:
 49→      "That\u2019s all you need. Let\u2019s build something remarkable.",
 50→    target: null,
 51→    position: "center",
 52→  },
 53→];
 54→
 55→const STORAGE_KEY = "apollo_onboarding_complete";
 56→const SPOTLIGHT_PADDING = 12;
 57→const CARD_GAP = 16;
 58→const EDGE_PADDING = 20;
 59→const CARD_WIDTH = 340;
 60→
 61→// ---------------------------------------------------------------------------
 62→// Helpers
 63→// ---------------------------------------------------------------------------
 64→
 65→/** Get the bounding rect for a tour target selector, or null. */
 66→function getTargetRect(selector: string | null): DOMRect | null {
 67→  if (!selector) return null;
 68→  const el = document.querySelector(selector);
 69→  if (!el) return null;
 70→  return el.getBoundingClientRect();
 71→}
 72→
 73→/** Compute padded spotlight rect from a target DOMRect. */
 74→function spotlightFromRect(rect: DOMRect) {
 75→  return {
 76→    x: rect.x - SPOTLIGHT_PADDING,
 77→    y: rect.y - SPOTLIGHT_PADDING,
 78→    width: rect.width + SPOTLIGHT_PADDING * 2,
 79→    height: rect.height + SPOTLIGHT_PADDING * 2,
 80→  };
 81→}
 82→
 83→/** Viewport-centre spotlight (for welcome/finish steps). */
 84→function centreSpotlight() {
 85→  const cx = window.innerWidth / 2;
 86→  const cy = window.innerHeight / 2;
