      // Audio whoosh on step change
      playWhoosh();
    } else {
      setSpotlight(target);
    }

    prevSpotlightRef.current = target;

    const cardH = cardRef.current?.offsetHeight ?? 180;
    setCardPos(
      computeCardPosition(isDesktop ? rect : null, step.position, cardH),
    );
  },
  [currentStep, isDesktop],
