 97→/** Euclidean distance between two spotlight centres. */
 98→function spotlightDistance(
 99→  a: { x: number; y: number; width: number; height: number },
100→  b: { x: number; y: number; width: number; height: number },
101→) {
102→  const ax = a.x + a.width / 2;
103→  const ay = a.y + a.height / 2;
104→  const bx = b.x + b.width / 2;
105→  const by = b.y + b.height / 2;
106→  return Math.sqrt((ax - bx) ** 2 + (ay - by) ** 2);
107→}
108→
109→/** Expand a spotlight rect by a factor around its centre (lens flare). */
110→function inflateSpotlight(
111→  s: { x: number; y: number; width: number; height: number },
112→  factor: number,
113→) {
114→  const cx = s.x + s.width / 2;
115→  const cy = s.y + s.height / 2;
116→  const w = s.width * factor;
117→  const h = s.height * factor;
118→  return { x: cx - w / 2, y: cy - h / 2, width: w, height: h };
119→}
