+  b: { x: number; y: number; width: number; height: number },
+) {
+  const ax = a.x + a.width / 2;
+  const ay = a.y + a.height / 2;
+  const bx = b.x + b.width / 2;
+  const by = b.y + b.height / 2;
+  return Math.sqrt((ax - bx) ** 2 + (ay - by) ** 2);
