    case "bottom":
      x = targetRect.left + targetRect.width / 2 - CARD_WIDTH / 2;
      y = targetRect.bottom + pad + CARD_GAP;
      break;
    case "top":
      x = targetRect.left + targetRect.width / 2 - CARD_WIDTH / 2;
      y = targetRect.top - pad - CARD_GAP - cardHeight;
      break;
    case "right":
      x = targetRect.right + pad + CARD_GAP;
      y = targetRect.top + targetRect.height / 2 - cardHeight / 2;
      break;
    case "left":
      x = targetRect.left - pad - CARD_GAP - CARD_WIDTH;
      y = targetRect.top + targetRect.height / 2 - cardHeight / 2;
      break;
  }

  // Clamp to viewport
  x = Math.max(EDGE_PADDING, Math.min(x, window.innerWidth - CARD_WIDTH - EDGE_PADDING));
  y = Math.max(EDGE_PADDING, Math.min(y, window.innerHeight - cardHeight - EDGE_PADDING));

  return { x, y };
}

// ---------------------------------------------------------------------------
// Component
// ---------------------------------------------------------------------------

export function TourOverlay() {
  const [currentStep, setCurrentStep] = useState(0);
  const [visible, setVisible] = useState(false);
  const [exiting, setExiting] = useState(false);
  const [isDesktop, setIsDesktop] = useState(true);
  const [spotlight, setSpotlight] = useState(centreSpotlight);
  const [cardPos, setCardPos] = useState<CardPos>({ x: 0, y: 0 });
  const cardRef = useRef<HTMLDivElement>(null);
  const prevSpotlightRef = useRef(centreSpotlight());

  // ── Check localStorage on mount ──
  useEffect(() => {
    const completed = localStorage.getItem(STORAGE_KEY);
    if (!completed) {
      setVisible(true);
    }
  }, []);

  // ── Responsive check ──
  useEffect(() => {
    const check = () => setIsDesktop(window.innerWidth >= 768);
    check();
    window.addEventListener("resize", check);
    return () => window.removeEventListener("resize", check);
  }, []);

  // ── Calculate spotlight + card position ──
  const recalculate = useCallback(() => {
    const step = TOUR_STEPS[currentStep];
    const rect = getTargetRect(step.target);

    if (rect && isDesktop) {
      setSpotlight(spotlightFromRect(rect));
    } else {
      setSpotlight(centreSpotlight());
    }

    const cardH = cardRef.current?.offsetHeight ?? 180;
    setCardPos(computeCardPosition(isDesktop ? rect : null, step.position, cardH));
  }, [currentStep, isDesktop]);

  // Recalculate on step change + initial
  useEffect(() => {
    // Small delay to let DOM settle after step change
    const id = requestAnimationFrame(recalculate);
    return () => cancelAnimationFrame(id);
  }, [recalculate]);

  // ── Resize + ResizeObserver ──
  useEffect(() => {
    if (!visible) return;

    const onResize = () => recalculate();
    window.addEventListener("resize", onResize);

    // Observe the active target element for layout changes
    const step = TOUR_STEPS[currentStep];
    let observer: ResizeObserver | undefined;
    if (step.target) {
      const el = document.querySelector(step.target);
      if (el) {
        observer = new ResizeObserver(onResize);
        observer.observe(el);
      }
    }

    return () => {
      window.removeEventListener("resize", onResize);
      observer?.disconnect();
    };
  }, [visible, currentStep, recalculate]);

  // ── Keyboard navigation ──
  useEffect(() => {
    if (!visible || exiting) return;
    const onKey = (e: KeyboardEvent) => {
      if (e.key === "ArrowRight" || e.key === "Enter") {
        e.preventDefault();
        handleNext();
      } else if (e.key === "ArrowLeft") {
        e.preventDefault();
        handlePrev();
      } else if (e.key === "Escape") {
        e.preventDefault();
        handleDismiss();
      }
    };
    window.addEventListener("keydown", onKey);
    return () => window.removeEventListener("keydown", onKey);
  });

  // ── Navigation handlers ──
  const handleNext = () => {
    if (currentStep < TOUR_STEPS.length - 1) {
      setCurrentStep((s) => s + 1);
    } else {
      handleDismiss();
    }
  };

  const handlePrev = () => {
    if (currentStep > 0) {
      setCurrentStep((s) => s - 1);
    }
  };

  const handleDismiss = () => {
    if (exiting) return;
    setExiting(true);
    // Aperture open: expand spotlight to full viewport
    setSpotlight(fullSpotlight());
    // Fade out after aperture opens
    setTimeout(() => {
      setVisible(false);
      setExiting(false);
      localStorage.setItem(STORAGE_KEY, "true");
    }, 500);
  };

  if (!visible) return null;

  const step = TOUR_STEPS[currentStep];
  const isFirst = currentStep === 0;
  const isLast = currentStep === TOUR_STEPS.length - 1;
  const hasTarget = step.target !== null && isDesktop;

  // Spring config for the spotlight morph
  const spotlightSpring = {
    type: "spring" as const,
    stiffness: 170,
    damping: 26,
  };

  return (
    <AnimatePresence>
      {visible && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: exiting ? 0 : 1 }}
          transition={{ duration: exiting ? 0.3 : 0.4 }}
          className="fixed inset-0 z-[100]"
          style={{ pointerEvents: "none" }}
        >
          {/* ── SVG Mask Overlay (desktop only) ── */}
          {isDesktop && (
            <svg
              className="pointer-events-none fixed inset-0"
              style={{ width: "100vw", height: "100vh", zIndex: 100 }}
            >
              <defs>
                <filter id="spotlight-feather">
                  <feGaussianBlur stdDeviation="10" />
                </filter>
                <mask id="spotlight-mask">
                  <rect width="100%" height="100%" fill="white" />
                  <motion.rect
                    animate={{
                      x: spotlight.x,
                      y: spotlight.y,
                      width: spotlight.width,
                      height: spotlight.height,
                    }}
                    transition={spotlightSpring}
                    rx={16}
                    fill="black"
                    filter="url(#spotlight-feather)"
                  />
                </mask>
              </defs>
              <rect
                width="100%"
                height="100%"
                fill="rgba(0,0,0,0.55)"
                mask="url(#spotlight-mask)"
              />
            </svg>
          )}

          {/* ── Mobile scrim (no spotlight) ── */}
          {!isDesktop && (
            <div className="pointer-events-none fixed inset-0 bg-black/50" style={{ zIndex: 100 }} />
          )}

          {/* ── Breathing Glow Ring (desktop with target only) ── */}
          {isDesktop && hasTarget && !exiting && (
            <motion.div
              className="pointer-events-none fixed"
              style={{
                zIndex: 101,
                borderRadius: 16,
                animation: "tour-breathe 3s ease-in-out infinite",
              }}
              animate={{
                x: spotlight.x,
                y: spotlight.y,
                width: spotlight.width,
                height: spotlight.height,
              }}
              transition={spotlightSpring}
            />
          )}

          {/* ── Glass Tooltip Card ── */}
          <AnimatePresence mode="wait">
            <motion.div
              key={currentStep}
              ref={cardRef}
              initial={{ opacity: 0, y: 12 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -8 }}
              transition={{
                type: "spring",
                stiffness: 300,
                damping: 30,
                delay: hasTarget ? 0.15 : 0,
              }}
              className="fixed rounded-2xl border border-white/40 bg-white/85 p-5 shadow-xl backdrop-blur-[20px]"
              style={{
                zIndex: 102,
                width: CARD_WIDTH,
                left: isDesktop ? cardPos.x : "50%",
                top: isDesktop ? cardPos.y : "50%",
                transform: isDesktop ? undefined : "translate(-50%, -50%)",
                pointerEvents: "auto",
              }}
            >
              {/* Progress dots — 3-tier hierarchy */}
              <div className="mb-4 flex items-center justify-center gap-2">
                {TOUR_STEPS.map((_, i) => (
                  <div
                    key={i}
                    className={`rounded-full transition-all duration-300 ${
                      i === currentStep
                        ? "h-2 w-2 bg-[#2F2F2F]"
                        : i < currentStep
                          ? "h-1.5 w-1.5 bg-[#8B9D77]"
                          : "h-1.5 w-1.5 bg-[#D1D1D1]"
                    }`}
                  />
                ))}
              </div>

              <h3 className="font-serif text-base font-semibold text-[#2F2F2F]">
                {step.title}
              </h3>
              <p className="mt-1.5 text-[13px] leading-relaxed text-[#6B6B6B]">
                {step.description}
              </p>

              {/* Navigation */}
              <div className="mt-5 flex items-center justify-between">
                <button
                  onClick={handleDismiss}
                  className="text-[13px] text-[#6B6B6B] transition-colors hover:text-[#2F2F2F]"
                >
                  Skip tour
                </button>
                <div className="flex items-center gap-2">
                  {!isFirst && (
                    <button
                      onClick={handlePrev}
                      className="rounded-lg px-3 py-1.5 text-[13px] text-[#6B6B6B] transition-colors hover:bg-black/5 hover:text-[#2F2F2F]"
                    >
                      Back
                    </button>
                  )}
                  <button
                    onClick={handleNext}
                    className="rounded-lg bg-[#2F2F2F] px-4 py-1.5 text-[13px] font-medium text-white transition-colors hover:bg-[#1A1A1A]"
                  >
                    {isLast ? "Get Started" : "Next"}
                  </button>
                </div>
              </div>
            </motion.div>
          </AnimatePresence>
        </motion.div>
      )}
    </AnimatePresence>
  );
}

