121→/** Play a subtle synthesised whoosh via Web Audio API. */
122→function playWhoosh() {
123→  try {
124→    const ctx = new AudioContext();
125→    const osc = ctx.createOscillator();
126→    const gain = ctx.createGain();
127→    osc.type = "sine";
128→    osc.frequency.setValueAtTime(600, ctx.currentTime);
129→    osc.frequency.exponentialRampToValueAtTime(200, ctx.currentTime + 0.15);
130→    gain.gain.setValueAtTime(0.04, ctx.currentTime);
131→    gain.gain.exponentialRampToValueAtTime(0.001, ctx.currentTime + 0.2);
132→    osc.connect(gain).connect(ctx.destination);
133→    osc.start(ctx.currentTime);
134→    osc.stop(ctx.currentTime + 0.2);
135→    // Auto-close context after sound finishes
136→    setTimeout(() => ctx.close(), 300);
137→  } catch {
138→    // Audio not available — fail silently
139→  }
140→}
141→
142→interface CardPos {
143→  x: number;
144→  y: number;
145→}
146→
147→/** Smart card positioning with viewport clamping. */
148→function computeCardPosition(
149→  targetRect: DOMRect | null,
150→  preference: TourStep["position"],
151→  cardHeight: number,
152→): CardPos {
153→  // Centre fallback
154→  if (!targetRect || preference === "center") {
155→    return {
156→      x: window.innerWidth / 2 - CARD_WIDTH / 2,
157→      y: window.innerHeight / 2 - cardHeight / 2,
158→    };
159→  }
160→
161→  const pad = SPOTLIGHT_PADDING;
162→  let x: number;
163→  let y: number;
164→
165→  switch (preference) {
166→    case "bottom":
167→      x = targetRect.left + targetRect.width / 2 - CARD_WIDTH / 2;
168→      y = targetRect.bottom + pad + CARD_GAP;
169→      break;
170→    case "top":
171→      x = targetRect.left + targetRect.width / 2 - CARD_WIDTH / 2;
172→      y = targetRect.top - pad - CARD_GAP - cardHeight;
173→      break;
174→    case "right":
175→      x = targetRect.right + pad + CARD_GAP;
176→      y = targetRect.top + targetRect.height / 2 - cardHeight / 2;
177→      break;
178→    case "left":
179→      x = targetRect.left - pad - CARD_GAP - CARD_WIDTH;
180→      y = targetRect.top + targetRect.height / 2 - cardHeight / 2;
181→      break;
182→  }
183→
184→  // Clamp to viewport
185→  x = Math.max(EDGE_PADDING, Math.min(x, window.innerWidth - CARD_WIDTH - EDGE_PADDING));
186→  y = Math.max(EDGE_PADDING, Math.min(y, window.innerHeight - cardHeight - EDGE_PADDING));
187→
188→  return { x, y };
189→}
