195→export function TourOverlay() {
196→  const [currentStep, setCurrentStep] = useState(0);
197→  const [visible, setVisible] = useState(false);
198→  const [exiting, setExiting] = useState(false);
199→  const [isDesktop, setIsDesktop] = useState(true);
200→  const [spotlight, setSpotlight] = useState(centreSpotlight);
201→  const [cardPos, setCardPos] = useState<CardPos>({ x: 0, y: 0 });
202→  const cardRef = useRef<HTMLDivElement>(null);
203→  const prevSpotlightRef = useRef(centreSpotlight());
204→
205→  // ── Check localStorage on mount ──
206→  useEffect(() => {
207→    const completed = localStorage.getItem(STORAGE_KEY);
208→    if (!completed) {
209→      setVisible(true);
210→    }
211→  }, []);
212→
213→  // ── Responsive check ──
214→  useEffect(() => {
215→    const check = () => setIsDesktop(window.innerWidth >= 768);
216→    check();
217→    window.addEventListener("resize", check);
218→    return () => window.removeEventListener("resize", check);
219→  }, []);
220→
221→  // ── Calculate spotlight + card position (with lens flare for long morphs) ──
222→  const recalculate = useCallback(
223→    (withFlare = false) => {
224→      const step = TOUR_STEPS[currentStep];
225→      const rect = getTargetRect(step.target);
226→      const target =
227→        rect && isDesktop ? spotlightFromRect(rect) : centreSpotlight();
228→
229→      if (withFlare && isDesktop) {
230→        const dist = spotlightDistance(prevSpotlightRef.current, target);
231→        if (dist > LENS_FLARE_DISTANCE_THRESHOLD) {
232→          // Lens flare: briefly inflate mid-transit, then settle
233→          const midpoint = {
234→            x: (prevSpotlightRef.current.x + target.x) / 2,
235→            y: (prevSpotlightRef.current.y + target.y) / 2,
236→            width: (prevSpotlightRef.current.width + target.width) / 2,
237→            height: (prevSpotlightRef.current.height + target.height) / 2,
238→          };
239→          setSpotlight(inflateSpotlight(midpoint, LENS_FLARE_SCALE));
240→          setTimeout(() => setSpotlight(target), 180);
241→        } else {
242→          setSpotlight(target);
243→        }
244→        // Audio whoosh on step change
245→        playWhoosh();
246→      } else {
247→        setSpotlight(target);
248→      }
249→
250→      prevSpotlightRef.current = target;
251→
252→      const cardH = cardRef.current?.offsetHeight ?? 180;
253→      setCardPos(
254→        computeCardPosition(isDesktop ? rect : null, step.position, cardH),
255→      );
256→    },
257→    [currentStep, isDesktop],
258→  );
259→
260→  // Track whether this is the initial mount (no flare) vs step change (with flare)
261→  const isInitialMount = useRef(true);
262→
263→  // Recalculate on step change + initial
264→  useEffect(() => {
265→    const flare = !isInitialMount.current;
266→    isInitialMount.current = false;
267→    const id = requestAnimationFrame(() => recalculate(flare));
268→    return () => cancelAnimationFrame(id);
269→  }, [recalculate]);
270→
271→  // ── Resize + ResizeObserver ──
272→  useEffect(() => {
273→    if (!visible) return;
274→
275→    const onResize = () => recalculate();
276→    window.addEventListener("resize", onResize);
277→
278→    // Observe the active target element for layout changes
279→    const step = TOUR_STEPS[currentStep];
280→    let observer: ResizeObserver | undefined;
281→    if (step.target) {
282→      const el = document.querySelector(step.target);
283→      if (el) {
284→        observer = new ResizeObserver(onResize);
285→        observer.observe(el);
286→      }
287→    }
288→
289→    return () => {
290→      window.removeEventListener("resize", onResize);
291→      observer?.disconnect();
292→    };
293→  }, [visible, currentStep, recalculate]);
294→
295→  // ── Interactive target: click through spotlight to advance ──
296→  useEffect(() => {
297→    if (!visible || exiting) return;
298→    const step = TOUR_STEPS[currentStep];
299→    if (!step.target) return;
300→    const el = document.querySelector(step.target);
301→    if (!el) return;
302→    const onClick = () => handleNext();
303→    el.addEventListener("click", onClick, { once: true });
304→    return () => el.removeEventListener("click", onClick);
305→  });
306→
307→  // ── Keyboard navigation ──
308→  useEffect(() => {
309→    if (!visible || exiting) return;
310→    const onKey = (e: KeyboardEvent) => {
311→      if (e.key === "ArrowRight" || e.key === "Enter") {
312→        e.preventDefault();
313→        handleNext();
314→      } else if (e.key === "ArrowLeft") {
315→        e.preventDefault();
316→        handlePrev();
317→      } else if (e.key === "Escape") {
318→        e.preventDefault();
319→        handleDismiss();
320→      }
321→    };
322→    window.addEventListener("keydown", onKey);
323→    return () => window.removeEventListener("keydown", onKey);
324→  });
325→
326→  // ── Navigation handlers ──
327→  const handleNext = () => {
328→    if (currentStep < TOUR_STEPS.length - 1) {
329→      setCurrentStep((s) => s + 1);
330→    } else {
331→      handleDismiss();
332→    }
333→  };
334→
335→  const handlePrev = () => {
336→    if (currentStep > 0) {
337→      setCurrentStep((s) => s - 1);
338→    }
339→  };
340→
341→  const handleDismiss = () => {
342→    if (exiting) return;
343→    setExiting(true);
344→    // Aperture open: expand spotlight to full viewport
345→    setSpotlight(fullSpotlight());
346→    // Fade out after aperture opens
347→    setTimeout(() => {
348→      setVisible(false);
349→      setExiting(false);
350→      localStorage.setItem(STORAGE_KEY, "true");
351→    }, 500);
352→  };
353→
354→  if (!visible) return null;
355→
356→  const step = TOUR_STEPS[currentStep];
357→  const isFirst = currentStep === 0;
358→  const isLast = currentStep === TOUR_STEPS.length - 1;
359→  const hasTarget = step.target !== null && isDesktop;
360→
361→  // Spring config for the spotlight morph
362→  const spotlightSpring = {
363→    type: "spring" as const,
364→    stiffness: 170,
365→    damping: 26,
366→  };
367→
368→  return (
369→    <AnimatePresence>
370→      {visible && (
371→        <motion.div
372→          initial={{ opacity: 0 }}
373→          animate={{ opacity: exiting ? 0 : 1 }}
374→          transition={{ duration: exiting ? 0.3 : 0.4 }}
375→          className="fixed inset-0 z-[100]"
376→          style={{ pointerEvents: "none" }}
377→        >
378→          {/* ── SVG Mask Overlay (desktop only) ── */}
379→          {isDesktop && (
380→            <svg
381→              className="pointer-events-none fixed inset-0"
