462→              <motion.div
463→                animate={{ x: [0, 1.5, -1, 2, -1.5, 0], y: [0, -1, 2, -1.5, 1, 0] }}
464→                transition={{ duration: 10, repeat: Infinity, ease: "easeInOut" }}
465→              >
466→                {/* Progress dots — 3-tier hierarchy */}
467→                <div className="mb-4 flex items-center justify-center gap-2">
468→                  {TOUR_STEPS.map((_, i) => (
469→                    <div
470→                      key={i}
