  1→"use client";
  2→
  3→import { useState, useCallback, useRef, useEffect } from "react";
  4→import { Document, Page, pdfjs } from "react-pdf";
  5→import { ChevronLeft, ChevronRight, ZoomIn, ZoomOut, PanelLeftClose, PanelLeftOpen, Download } from "lucide-react";
  6→import { Button } from "@/components/ui/button";
  7→
  8→import "react-pdf/dist/Page/AnnotationLayer.css";
  9→import "react-pdf/dist/Page/TextLayer.css";
 10→
 11→// Match worker version to installed pdfjs-dist package
 12→pdfjs.GlobalWorkerOptions.workerSrc = `https://unpkg.com/pdfjs-dist@${pdfjs.version}/build/pdf.worker.min.mjs`;
 13→
 14→interface PdfViewerProps {
 15→  url: string | null;
 16→  isSandbox?: boolean;
 17→  projectId?: string;
 18→}
 19→
 20→function LazyThumbnail({
 21→  pageNum,
 22→  isActive,
 23→  onClick,
 24→}: {
 25→  pageNum: number;
 26→  isActive: boolean;
 27→  onClick: () => void;
 28→}) {
 29→  const ref = useRef<HTMLButtonElement>(null);
 30→  const [isVisible, setIsVisible] = useState(false);
 31→
 32→  useEffect(() => {
 33→    const el = ref.current;
 34→    if (!el) return;
 35→    const observer = new IntersectionObserver(
 36→      ([entry]) => {
 37→        if (entry.isIntersecting) setIsVisible(true);
 38→      },
 39→      { rootMargin: "200px" }
 40→    );
 41→    observer.observe(el);
 42→    return () => observer.disconnect();
 43→  }, []);
 44→
 45→  return (
 46→    <button
 47→      ref={ref}
 48→      onClick={onClick}
 49→      className={`rounded border transition-all ${
 50→        isActive
 51→          ? "border-[#8B9D77] ring-1 ring-[#8B9D77]/30"
 52→          : "border-transparent hover:border-[#D1D1D1]"
 53→      }`}
 54→    >
 55→      {isVisible ? (
 56→        <Page
 57→          pageNumber={pageNum}
 58→          width={72}
 59→          renderTextLayer={false}
 60→          renderAnnotationLayer={false}
 61→        />
 62→      ) : (
 63→        <div className="h-[102px] w-[72px] rounded bg-[#E8E8E8]" />
 64→      )}
 65→      <span className="block text-center text-[9px] text-[#6B6B6B]">
 66→        {pageNum}
 67→      </span>
 68→    </button>
 69→  );
 70→}
 71→
 72→export function PdfViewer({ url, isSandbox, projectId }: PdfViewerProps) {
 73→  const [numPages, setNumPages] = useState<number>(0);
 74→  const [pageNumber, setPageNumber] = useState(1);
 75→  const [scale, setScale] = useState(1.0);
 76→  const [showThumbnails, setShowThumbnails] = useState(false);
 77→
 78→  const onDocumentLoadSuccess = useCallback(
 79→    ({ numPages: total }: { numPages: number }) => {
 80→      setNumPages(total);
 81→      setPageNumber(1);
 82→    },
 83→    []
 84→  );
 85→
 86→  if (!url) {
 87→    return (
 88→      <div className="flex h-full min-h-[400px] items-center justify-center rounded-2xl bg-[#FDFDFD] p-8">
 89→        <div className="text-center">
 90→          <p className="font-serif text-lg text-[#D1D1D1]">
 91→            Compiled PDF Preview
 92→          </p>
 93→          <p className="mt-2 text-sm text-[#D1D1D1]">
 94→            Compile the thesis to generate a preview.
 95→          </p>
 96→        </div>
 97→      </div>
 98→    );
 99→  }
100→
101→  return (
102→    <div className="relative flex flex-col gap-2 p-4">
103→      {/* Sandbox watermark overlay (matches Ghostscript watermark in compiled PDF) */}
104→      {isSandbox && (
105→        <div className="pointer-events-none absolute inset-0 z-10 flex items-center justify-center">
106→          <div className="rotate-[-45deg] select-none font-serif text-9xl italic tracking-[0.2em] text-black/[0.15]">
107→            Apollo
108→          </div>
109→        </div>
110→      )}
111→
112→      {/* Glass pill controls */}
113→      <div className="flex items-center justify-between rounded-full border border-white/30 bg-white/80 px-4 py-2 backdrop-blur-[20px]">
114→        <div className="flex items-center gap-1">
115→          {/* Thumbnails toggle */}
116→          <Button
117→            size="sm"
118→            variant="ghost"
119→            onClick={() => setShowThumbnails((t) => !t)}
120→            className="h-7 w-7 p-0"
121→            title={showThumbnails ? "Hide thumbnails" : "Show thumbnails"}
122→          >
123→            {showThumbnails ? (
124→              <PanelLeftClose className="h-4 w-4" />
125→            ) : (
126→              <PanelLeftOpen className="h-4 w-4" />
127→            )}
128→          </Button>
129→          <div className="mx-1 h-4 w-px bg-[#E5E5E5]" />
130→          <Button
131→            size="sm"
132→            variant="ghost"
133→            onClick={() => setPageNumber((p) => Math.max(1, p - 1))}
134→            disabled={pageNumber <= 1}
135→            className="h-7 w-7 p-0"
136→          >
137→            <ChevronLeft className="h-4 w-4" />
138→          </Button>
139→          <span className="font-mono text-xs text-[#6B6B6B]">
140→            {pageNumber} / {numPages}
141→          </span>
142→          <Button
143→            size="sm"
144→            variant="ghost"
145→            onClick={() => setPageNumber((p) => Math.min(numPages, p + 1))}
146→            disabled={pageNumber >= numPages}
147→            className="h-7 w-7 p-0"
148→          >
149→            <ChevronRight className="h-4 w-4" />
150→          </Button>
151→        </div>
152→        <div className="flex items-center gap-1">
153→          <Button
154→            size="sm"
155→            variant="ghost"
156→            onClick={() => setScale((s) => Math.max(0.5, s - 0.1))}
157→            disabled={scale <= 0.5}
158→            className="h-7 w-7 p-0"
159→          >
160→            <ZoomOut className="h-4 w-4" />
161→          </Button>
162→          <span className="font-mono text-xs text-[#6B6B6B]">
163→            {Math.round(scale * 100)}%
164→          </span>
165→          <Button
166→            size="sm"
167→            variant="ghost"
168→            onClick={() => setScale((s) => Math.min(2.0, s + 0.1))}
169→            disabled={scale >= 2.0}
170→            className="h-7 w-7 p-0"
171→          >
172→            <ZoomIn className="h-4 w-4" />
173→          </Button>
174→          {projectId && (
175→            <>
176→              <div className="mx-1 h-4 w-px bg-[#E5E5E5]" />
177→              <Button
178→                size="sm"
179→                variant="ghost"
180→                onClick={() =>
181→                  window.open(
182→                    `/api/projects/${projectId}/preview.pdf?download=1`,
183→                    "_blank"
184→                  )
185→                }
186→                className="h-7 w-7 p-0"
187→                title="Download PDF"
188→              >
189→                <Download className="h-4 w-4" />
190→              </Button>
191→            </>
192→          )}
193→        </div>
194→      </div>
195→
196→      <div className="flex gap-2">
197→        {/* Page thumbnails sidebar */}
198→        {showThumbnails && numPages > 0 && (
199→          <div
200→            className="flex w-24 shrink-0 flex-col gap-2 overflow-y-auto rounded-lg bg-[#F5F5F5] p-2"
201→            style={{ maxHeight: "70vh", scrollbarWidth: "thin", scrollbarColor: "rgba(0,0,0,0.1) transparent" }}
202→          >
203→            <Document file={url}>
204→              {Array.from({ length: numPages }, (_, i) => (
205→                <LazyThumbnail
206→                  key={i + 1}
207→                  pageNum={i + 1}
208→                  isActive={pageNumber === i + 1}
209→                  onClick={() => setPageNumber(i + 1)}
210→                />
211→              ))}
212→            </Document>
213→          </div>
214→        )}
215→
216→        {/* PDF Document */}
217→        <div
218→          className="flex-1 overflow-auto rounded-2xl bg-white transition-opacity duration-200"
219→          style={{ scrollbarWidth: "thin", scrollbarColor: "rgba(0,0,0,0.1) transparent" }}
220→        >
221→          <Document
222→            file={url}
223→            onLoadSuccess={onDocumentLoadSuccess}
224→            loading={
225→              <div className="flex h-64 items-center justify-center">
226→                <p className="font-mono text-sm text-[#D1D1D1]">Loading PDF\u2026</p>
227→              </div>
228→            }
229→            error={
230→              <div className="flex h-64 items-center justify-center">
231→                <p className="text-sm text-destructive">Failed to load PDF</p>
232→              </div>
233→            }
234→          >
235→            <Page pageNumber={pageNumber} scale={scale} />
236→          </Document>
237→        </div>
238→      </div>
239→    </div>
240→  );
241→}
