  1→import { execFile } from "child_process";
  2→import { promisify } from "util";
  3→import { readFile, writeFile, mkdir, rm, copyFile } from "fs/promises";
  4→import path from "path";
  5→import os from "os";
  6→import { parseLatexLog } from "./parse-log";
  7→import type { ParsedLog } from "./parse-log";
  8→
  9→const execFileAsync = promisify(execFile);
 10→
 11→export type CompileMode = "docker" | "local" | "mock";
 12→
 13→export interface CompileResult {
 14→  success: boolean;
 15→  pdfPath: string | null;
 16→  log: ParsedLog;
 17→  rawLog: string;
 18→  compileTimeMs: number;
 19→}
 20→
 21→function getCompileMode(): CompileMode {
 22→  const mode = process.env.LATEX_COMPILE_MODE;
 23→  if (mode === "docker" || mode === "local" || mode === "mock") return mode;
 24→  return "mock";
 25→}
 26→
 27→/**
 28→ * Compile LaTeX source into a PDF.
 29→ */
 30→export async function compileTex(
 31→  texContent: string,
 32→  options: {
 33→    projectId: string;
 34→    watermark?: boolean;
 35→    draftFooter?: boolean;
 36→    clsFiles?: string[];
 37→    bstFile?: string;
 38→    bibContent?: string;
 39→    chapterFiles?: Record<string, string>;
 40→    /** Paths to figure files (relative path → absolute source path) */
 41→    figureFiles?: Record<string, string>;
 42→  }
 43→): Promise<CompileResult> {
 44→  const mode = getCompileMode();
 45→
 46→  if (mode === "mock") {
 47→    return mockCompile(texContent);
 48→  }
 49→
 50→  if (mode === "docker") {
 51→    return dockerCompile(texContent, options);
 52→  }
 53→
 54→  return localCompile(texContent, options);
 55→}
 56→
 57→async function mockCompile(texContent: string): Promise<CompileResult> {
 58→  // For testing: validate basic TeX structure
 59→  const hasDocumentClass = /\\documentclass/.test(texContent);
 60→  const hasBeginDocument = /\\begin\{document\}/.test(texContent);
 61→  const hasEndDocument = /\\end\{document\}/.test(texContent);
 62→
 63→  const errors: string[] = [];
 64→  if (!hasDocumentClass) errors.push("Missing \\documentclass");
 65→  if (!hasBeginDocument) errors.push("Missing \\begin{document}");
 66→  if (!hasEndDocument) errors.push("Missing \\end{document}");
 67→
 68→  const success = errors.length === 0;
 69→
 70→  // In mock mode, write a minimal placeholder PDF so the compile route sees a pdfPath
 71→  let pdfPath: string | null = null;
 72→  if (success) {
 73→    const persistDir = path.join(os.tmpdir(), "apollo-pdfs");
 74→    await mkdir(persistDir, { recursive: true });
 75→    pdfPath = path.join(persistDir, "mock.pdf");
 76→    // Minimal valid PDF (1-page blank)
 77→    const minimalPdf = `%PDF-1.0\n1 0 obj<</Pages 2 0 R>>endobj\n2 0 obj<</Kids[3 0 R]/Count 1>>endobj\n3 0 obj<</MediaBox[0 0 612 792]>>endobj\ntrailer<</Root 1 0 R>>\n`;
 78→    await writeFile(pdfPath, minimalPdf);
 79→  }
 80→
 81→  return {
 82→    success,
 83→    pdfPath,
 84→    log: { errors, warnings: [], errorCount: errors.length, warningCount: 0, structuredErrors: [] },
 85→    rawLog: success ? "Mock compilation successful" : errors.join("\n"),
 86→    compileTimeMs: 1,
 87→  };
 88→}
 89→
 90→async function dockerCompile(
 91→  texContent: string,
 92→  options: {
 93→    projectId: string;
 94→    watermark?: boolean;
 95→    draftFooter?: boolean;
 96→    clsFiles?: string[];
 97→    bstFile?: string;
 98→    bibContent?: string;
 99→    chapterFiles?: Record<string, string>;
100→    figureFiles?: Record<string, string>;
101→  }
102→): Promise<CompileResult> {
103→  const start = Date.now();
104→  const workDir = path.join(os.tmpdir(), `apollo-compile-${options.projectId}`);
105→
106→  try {
107→    await mkdir(workDir, { recursive: true });
108→
109→    // Write tex content.
110→    // Sandbox watermark is handled post-compile by Ghostscript (watermark.sh in Docker),
111→    // so only inject LaTeX watermark for draft_footer mode.
112→    const dockerTexOptions = { watermark: false, draftFooter: options.draftFooter ?? false };
113→    await writeFile(path.join(workDir, "main.tex"), injectWatermarkPackage(texContent, dockerTexOptions));
114→
115→    // Copy CLS and BST files from templates
116→    const templatesDir = path.resolve(process.cwd(), "../../templates");
117→    const clsFiles = options.clsFiles ?? ["sskm-thesis.cls", "ssuhs-thesis.cls"];
118→
119→    for (const cls of clsFiles) {
120→      try {
121→        const content = await readFile(path.join(templatesDir, cls));
122→        await writeFile(path.join(workDir, cls), content);
123→      } catch {
124→        // CLS file may not exist locally in all environments
125→      }
126→    }
127→
128→    const bstFile = options.bstFile ?? "vancouver.bst";
129→    try {
130→      const bstContent = await readFile(path.join(templatesDir, bstFile));
131→      await writeFile(path.join(workDir, bstFile), bstContent);
132→    } catch {
133→      // BST may not exist locally
134→    }
135→
136→    // Copy logo directory
137→    try {
138→      const logoDir = path.join(templatesDir, "logo");
139→      await mkdir(path.join(workDir, "logo"), { recursive: true });
140→      const { stdout: logoFiles } = await execFileAsync("ls", [logoDir]);
141→      for (const file of logoFiles.trim().split("\n").filter(Boolean)) {
142→        const content = await readFile(path.join(logoDir, file));
143→        await writeFile(path.join(workDir, "logo", file), content);
144→      }
145→    } catch {
146→      // Logo dir may not exist
147→    }
148→
149→    // Write references.bib (populated or empty)
150→    await writeFile(path.join(workDir, "references.bib"), options.bibContent ?? "");
151→
152→    // Write chapter files
153→    if (options.chapterFiles) {
154→      await mkdir(path.join(workDir, "chapters"), { recursive: true });
155→      for (const [filename, content] of Object.entries(options.chapterFiles)) {
156→        await writeFile(path.join(workDir, filename), content);
157→      }
158→    }
159→
160→    // Copy figure files (relative path → absolute source) into workDir
161→    if (options.figureFiles) {
162→      for (const [relPath, srcPath] of Object.entries(options.figureFiles)) {
163→        try {
164→          const destPath = path.join(workDir, relPath);
165→          await mkdir(path.dirname(destPath), { recursive: true });
166→          await copyFile(srcPath, destPath);
167→        } catch {
168→          // Figure file may have been deleted — non-fatal
169→        }
170→      }
171→    }
172→
173→    // Create output directory
174→    const outputDir = path.join(workDir, "output");
175→    await mkdir(outputDir, { recursive: true });
176→
177→    // Run Docker compile
178→    const containerName = process.env.LATEX_CONTAINER_NAME ?? "apollo-latex";
179→
180→    // Security: network isolation + resource limits + Docker default seccomp.
181→    // Docker's built-in seccomp profile blocks dangerous syscalls (mount,
182→    // ptrace, reboot, kexec_load, etc.) and is well-tested. A custom whitelist
183→    // profile breaks container init (missing chdir, capget, epoll, etc.).
184→    const args = [
185→      "run", "--rm",
186→      "--network=none",
187→      "--tmpfs", "/tmp:rw,size=512m",
188→      "--memory=1g",
189→      "--pids-limit=256",
190→      "-v", `${workDir}:/thesis:rw`,
191→      containerName,
192→      // Sandbox watermark: Ghostscript overlay (uses built-in PostScript Palatino)
193→      ...(options.watermark ? ["--watermark"] : []),
194→    ];
195→
196→    const { stdout, stderr } = await execFileAsync("docker", args, {
197→      timeout: 120_000,
198→    });
199→
200→    // Persist PDF to a stable directory (temp workDir is cleaned up in finally)
201→    let pdfPath: string | null = null;
202→    try {
203→      const pdfData = await readFile(path.join(outputDir, "main.pdf"));
204→      const persistDir = path.join(os.tmpdir(), "apollo-pdfs");
205→      await mkdir(persistDir, { recursive: true });
206→      pdfPath = path.join(persistDir, `${options.projectId}.pdf`);
207→      await writeFile(pdfPath, pdfData);
208→    } catch {
209→      // PDF may not have been generated
210→    }
211→
212→    let rawLog = "";
213→    try {
214→      rawLog = await readFile(path.join(outputDir, "main.log"), "utf-8");
215→    } catch {
216→      rawLog = stdout + stderr;
217→    }
218→
219→    const log = parseLatexLog(rawLog);
220→
221→    return {
222→      success: log.errorCount === 0 && pdfPath !== null,
223→      pdfPath,
224→      log,
225→      rawLog,
226→      compileTimeMs: Date.now() - start,
227→    };
228→  } catch (err: unknown) {
229→    // execFileAsync includes stdout/stderr on the error object
230→    const execErr = err as { message?: string; stdout?: string; stderr?: string };
231→    const fullOutput = [execErr.stderr, execErr.stdout, execErr.message]
232→      .filter(Boolean)
233→      .join("\n");
234→    const log = parseLatexLog(fullOutput);
235→
236→    // If parseLatexLog found no errors, use the raw error message
237→    if (log.errorCount === 0) {
238→      log.errors.push(execErr.message ?? "Docker compilation failed");
239→      log.errorCount = 1;
240→    }
241→
242→    return {
243→      success: false,
244→      pdfPath: null,
245→      log,
246→      rawLog: fullOutput,
247→      compileTimeMs: Date.now() - start,
248→    };
249→  } finally {
250→    // Cleanup
251→    try {
252→      await rm(workDir, { recursive: true, force: true });
253→    } catch {
254→      // Best effort cleanup
255→    }
256→  }
257→}
