259→/**
260→ * Inject watermark commands AFTER \begin{document} so they override the CLS
261→ * defaults (both sskm-thesis.cls and ssuhs-thesis.cls already
262→ * \RequirePackage{draftwatermark} and set text to empty).
263→ */
264→function injectWatermarkPackage(
265→  texContent: string,
266→  options: { watermark?: boolean; draftFooter?: boolean }
267→): string {
268→  if (!options.watermark && !options.draftFooter) return texContent;
269→
270→  const marker = "\\begin{document}";
271→  const beginDocIdx = texContent.indexOf(marker);
272→  if (beginDocIdx === -1) return texContent;
273→
274→  const insertIdx = beginDocIdx + marker.length;
275→
276→  let cmds: string;
277→  if (options.watermark) {
278→    // Sandbox: diagonal "Apollo" watermark (uses document font — mathptmx/Times)
279→    cmds = "\n" + [
280→      "\\SetWatermarkText{\\textit{Apollo}}",
281→      "\\SetWatermarkColor[gray]{0.85}",
282→      "\\SetWatermarkScale{2.5}",
283→      "\\SetWatermarkAngle{45}",
284→    ].join("\n") + "\n";
285→  } else {
286→    // Licensed draft: smaller diagonal footer watermark
287→    cmds = "\n" + [
288→      "\\SetWatermarkText{\\textit{Generated with Apollo}}",
289→      "\\SetWatermarkColor[gray]{0.88}",
290→      "\\SetWatermarkScale{0.5}",
291→      "\\SetWatermarkAngle{45}",
292→    ].join("\n") + "\n";
293→  }
294→
295→  return texContent.slice(0, insertIdx) + cmds + texContent.slice(insertIdx);
296→}
297→
298→async function localCompile(
299→  texContent: string,
300→  options: {
301→    projectId: string;
302→    watermark?: boolean;
303→    draftFooter?: boolean;
304→    bibContent?: string;
305→    chapterFiles?: Record<string, string>;
306→    figureFiles?: Record<string, string>;
307→  }
308→): Promise<CompileResult> {
309→  const start = Date.now();
310→  const workDir = path.join(os.tmpdir(), `apollo-compile-${options.projectId}`);
311→
312→  try {
313→    await mkdir(workDir, { recursive: true });
314→    await writeFile(path.join(workDir, "main.tex"), injectWatermarkPackage(texContent, options));
315→
316→    // Copy templates
317→    const templatesDir = path.resolve(process.cwd(), "../../templates");
318→    for (const file of ["sskm-thesis.cls", "ssuhs-thesis.cls", "vancouver.bst"]) {
319→      try {
320→        const content = await readFile(path.join(templatesDir, file));
321→        await writeFile(path.join(workDir, file), content);
322→      } catch {
323→        // May not exist
324→      }
325→    }
326→
327→    // Copy logos
328→    try {
329→      await mkdir(path.join(workDir, "logo"), { recursive: true });
330→      const { stdout } = await execFileAsync("ls", [path.join(templatesDir, "logo")]);
331→      for (const file of stdout.trim().split("\n").filter(Boolean)) {
332→        const content = await readFile(path.join(templatesDir, "logo", file));
333→        await writeFile(path.join(workDir, "logo", file), content);
334→      }
335→    } catch {
336→      // May not exist
337→    }
338→
339→    // Write references.bib (populated or empty)
340→    await writeFile(path.join(workDir, "references.bib"), options.bibContent ?? "");
341→
342→    // Write chapter files
343→    if (options.chapterFiles) {
344→      await mkdir(path.join(workDir, "chapters"), { recursive: true });
345→      for (const [filename, content] of Object.entries(options.chapterFiles)) {
346→        await writeFile(path.join(workDir, filename), content);
347→      }
348→    }
349→
350→    // Copy figure files (relative path → absolute source) into workDir
351→    if (options.figureFiles) {
352→      for (const [relPath, srcPath] of Object.entries(options.figureFiles)) {
353→        try {
354→          const destPath = path.join(workDir, relPath);
355→          await mkdir(path.dirname(destPath), { recursive: true });
356→          await copyFile(srcPath, destPath);
357→        } catch {
358→          // Figure file may have been deleted — non-fatal
359→        }
360→      }
361→    }
362→
363→    // Run pdflatex → bibtex → pdflatex × 2
364→    const pdflatexArgs = ["-interaction=nonstopmode", "-output-directory", workDir, path.join(workDir, "main.tex")];
365→
366→    await execFileAsync("pdflatex", pdflatexArgs, { timeout: 120_000, cwd: workDir }).catch(() => {});
367→    await execFileAsync("bibtex", [path.join(workDir, "main")], { timeout: 30_000, cwd: workDir }).catch(() => {});
368→    await execFileAsync("pdflatex", pdflatexArgs, { timeout: 120_000, cwd: workDir }).catch(() => {});
369→    await execFileAsync("pdflatex", pdflatexArgs, { timeout: 120_000, cwd: workDir }).catch(() => {});
370→
371→    // Persist PDF to a stable directory
372→    let pdfPath: string | null = null;
373→    try {
374→      const pdfData = await readFile(path.join(workDir, "main.pdf"));
375→      const persistDir = path.join(os.tmpdir(), "apollo-pdfs");
376→      await mkdir(persistDir, { recursive: true });
377→      pdfPath = path.join(persistDir, `${options.projectId}.pdf`);
378→      await writeFile(pdfPath, pdfData);
379→    } catch {
380→      // PDF may not have been generated
381→    }
382→
383→    let rawLog = "";
384→    try {
385→      rawLog = await readFile(path.join(workDir, "main.log"), "utf-8");
386→    } catch {
387→      rawLog = "No log file generated";
388→    }
389→
390→    const log = parseLatexLog(rawLog);
391→
392→    return {
393→      success: log.errorCount === 0 && pdfPath !== null,
394→      pdfPath,
395→      log,
396→      rawLog,
397→      compileTimeMs: Date.now() - start,
398→    };
399→  } catch (err) {
400→    return {
401→      success: false,
402→      pdfPath: null,
403→      log: {
404→        errors: [err instanceof Error ? err.message : "Local compilation failed"],
405→        warnings: [],
406→        errorCount: 1,
407→        warningCount: 0,
408→        structuredErrors: [],
409→      },
410→      rawLog: err instanceof Error ? err.message : "Unknown error",
411→      compileTimeMs: Date.now() - start,
412→    };
413→  } finally {
414→    // Cleanup work directory (PDF already persisted)
415→    try {
416→      await rm(workDir, { recursive: true, force: true });
417→    } catch {
418→      // Best effort cleanup
419→    }
420→  }
421→}
