  1→import { NextRequest, NextResponse } from "next/server";
  2→import * as Sentry from "@sentry/nextjs";
  3→import { readFile, writeFile, mkdir, stat } from "fs/promises";
  4→import path from "path";
  5→import os from "os";
  6→import { getAuthenticatedUser } from "@/lib/api/auth";
  7→import {
  8→  unauthorised,
  9→  notFound,
 10→  internalError,
 11→  conflict,
 12→  queueFull,
 13→} from "@/lib/api/errors";
 14→import { tryAcquire, release } from "@/lib/compute/semaphore";
 15→import { createAdminSupabaseClient } from "@/lib/supabase/admin";
 16→import { assembleThesisContent } from "@/lib/latex/assemble";
 17→import { compileTex } from "@/lib/latex/compile";
 18→import { preflightChapter, aiValidateChapters } from "@/lib/latex/validate";
 19→import { uploadToR2, downloadFromR2 } from "@/lib/r2/client";
 20→import { checkLicenceForPhase } from "@/lib/api/licence-phase-gate";
 21→import type { Section, Citation, Figure, Compilation, Abbreviation } from "@/lib/types/database";
 22→
 23→/** Tmpdir location for figure downloads during compilation */
 24→const FIGURES_TMP_DIR = path.join(os.tmpdir(), "apollo-figures");
 25→
 26→export async function POST(
 27→  _request: NextRequest,
 28→  { params }: { params: Promise<{ id: string }> }
 29→) {
 30→  try {
 31→    const authResult = await getAuthenticatedUser();
 32→    if (!authResult) return unauthorised();
 33→
 34→    const { id } = await params;
 35→
 36→    // Licence phase gate — use current_phase for compile
 37→    const supabase = createAdminSupabaseClient();
 38→    const { data: projCheck } = await supabase
 39→      .from("projects")
 40→      .select("current_phase")
 41→      .eq("id", id)
 42→      .eq("user_id", authResult.user.id)
 43→      .single();
 44→
 45→    if (!projCheck) return notFound("Project not found");
 46→
 47→    const gateResult = await checkLicenceForPhase(
 48→      id, authResult.user.id, projCheck.current_phase as number, "compile"
 49→    );
 50→    if (gateResult instanceof NextResponse) return gateResult;
 51→
 52→    const typedProject = gateResult.project;
 53→
 54→    // Check for existing running compilation (with stale recovery)
 55→    const { data: runningCompilation } = await supabase
 56→      .from("compilations")
 57→      .select("id, created_at")
 58→      .eq("project_id", id)
 59→      .eq("status", "running")
 60→      .single();
 61→
 62→    if (runningCompilation) {
 63→      const STALE_COMPILE_MS = 5 * 60 * 1000; // 5 minutes
 64→      const createdAt = new Date(runningCompilation.created_at).getTime();
 65→      const age = Date.now() - createdAt;
 66→
 67→      if (age < STALE_COMPILE_MS) {
 68→        return conflict("A compilation is already in progress");
 69→      }
 70→
 71→      // Stale compilation — mark as failed and allow a new one
 72→      await supabase
 73→        .from("compilations")
 74→        .update({
 75→          status: "failed",
 76→          errors: ["Compilation timed out after 5 minutes"],
 77→        })
 78→        .eq("id", runningCompilation.id);
 79→    }
 80→
 81→    // Admission control — acquire compute slot
 82→    const slot = await tryAcquire("compile", id, authResult.user.id);
 83→    if (!slot.acquired) {
 84→      if (slot.position !== undefined) {
 85→        return queueFull(Math.ceil((slot.estimatedWaitMs ?? 30000) / 1000));
 86→      }
 87→      return queueFull();
 88→    }
 89→
 90→    try {
 91→      // Create compilation record
 92→      const { data: compilation, error: compilationError } = await supabase
 93→        .from("compilations")
 94→        .insert({
 95→          project_id: id,
 96→          trigger: "manual",
 97→          status: "running",
 98→          warnings: [],
 99→          errors: [],
100→        })
101→        .select("*")
102→        .single();
103→
104→      if (compilationError || !compilation) {
105→        console.error("Failed to create compilation record:", compilationError);
106→        return internalError("Failed to start compilation");
107→      }
108→
109→      // Read the template
110→      let template: string;
111→      try {
112→        const templatesDir = path.resolve(process.cwd(), "../../templates");
113→        template = await readFile(path.join(templatesDir, "main.tex"), "utf-8");
114→      } catch {
115→        await updateCompilation(supabase, compilation.id, {
116→          status: "failed",
117→          errors: ["Template file not found"],
118→        });
119→        return internalError("LaTeX template not found");
120→      }
121→
122→      // Fetch sections, citations, figures, and abbreviations for assembly
123→      const [sectionsResult, citationsResult, figuresResult, abbreviationsResult] = await Promise.all([
124→        supabase
125→          .from("sections")
126→          .select("*")
127→          .eq("project_id", id)
128→          .in("status", ["approved", "review"]),
129→        supabase
130→          .from("citations")
131→          .select("*")
132→          .eq("project_id", id),
133→        supabase
134→          .from("figures")
135→          .select("*")
136→          .eq("project_id", id),
137→        supabase
138→          .from("abbreviations")
139→          .select("*")
140→          .eq("project_id", id),
141→      ]);
142→
143→      const sections = (sectionsResult.data ?? []) as Section[];
144→      const citations = (citationsResult.data ?? []) as Citation[];
145→      const figures = (figuresResult.data ?? []) as Figure[];
146→      const abbreviations = (abbreviationsResult.data ?? []) as Abbreviation[];
147→
148→      // Resolve figure files: download from R2 to tmpdir, fall back to local disk
149→      const figureFiles: Record<string, string> = {};
150→      for (const fig of figures) {
151→        if (!fig.file_url) continue;
152→        // file_url is "figures/{projectId}/{analysisId}/{filename}"
153→        // R2 key is "projects/{projectId}/figures/{analysisId}/{filename}" (no double projectId)
154→        const afterProjectId = fig.file_url.replace(`figures/${id}/`, "");
155→        const r2Key = `projects/${id}/figures/${afterProjectId}`;
156→        const relAfterFigures = fig.file_url.replace(/^figures\//, "");
157→        const diskPath = path.join(FIGURES_TMP_DIR, relAfterFigures);
158→
159→        try {
160→          // Try R2 first
161→          const buffer = await downloadFromR2(r2Key);
162→          await mkdir(path.dirname(diskPath), { recursive: true });
163→          await writeFile(diskPath, buffer);
164→          figureFiles[fig.file_url] = diskPath;
165→        } catch {
166→          // Fall back to local disk (dev/test)
167→          try {
168→            await stat(diskPath);
169→            figureFiles[fig.file_url] = diskPath;
170→          } catch {
171→            // File unavailable — will cause a non-fatal warning during compile
172→          }
173→        }
174→      }
175→
176→      // Assemble thesis content (metadata + chapter files + BibTeX)
177→      const { tex, bib, chapterFiles, warnings: texWarnings } = assembleThesisContent(
178→        template,
179→        typedProject,
180→        sections,
181→        citations,
182→        abbreviations
183→      );
184→
185→      // Pre-flight validation on each chapter
186→      const preflightIssues = Object.entries(chapterFiles).flatMap(
187→        ([filename, content]) => preflightChapter(filename, content)
188→      );
189→
190→      const preflightErrors = preflightIssues.filter((i) => i.severity === "error");
191→      const preflightWarnings = preflightIssues.filter((i) => i.severity === "warning");
192→
193→      if (preflightErrors.length > 0) {
194→        await updateCompilation(supabase, compilation.id, {
195→          status: "failed",
196→          errors: preflightErrors.map(
197→            (i) => `${i.chapter}${i.line ? `:${i.line}` : ""}: ${i.message}`
198→          ),
199→          warnings: preflightWarnings.map(
200→            (i) => `${i.chapter}${i.line ? `:${i.line}` : ""}: ${i.message}`
201→          ),
202→        });
203→
204→        return NextResponse.json(
205→          {
206→            data: {
207→              compilation_id: compilation.id,
208→              status: "validation_failed",
209→              issues: preflightIssues,
210→              action: "Fix the issues in the section editor and retry",
211→            },
212→          },
213→          { status: 422 }
214→        );
215→      }
216→
217→      // AI validation (non-blocking — runs in parallel with compile setup)
218→      const aiValidationPromise = aiValidateChapters(chapterFiles);
219→
220→      // Determine watermark mode
221→      const watermarkMode = getWatermarkMode(typedProject.status, typedProject.current_phase);
222→      const result = await Sentry.startSpan(
223→        {
224→          name: "latex.compile",
225→          op: "compile.latex",
226→          attributes: {
227→            "project.id": id,
228→            "compile.watermark": watermarkMode,
229→            "compile.chapters": Object.keys(chapterFiles).length,
230→          },
231→        },
232→        () =>
233→          compileTex(tex, {
234→            projectId: id,
235→            watermark: watermarkMode === "sandbox",
236→            draftFooter: watermarkMode === "draft_footer",
237→            bibContent: bib,
238→            chapterFiles,
239→            figureFiles,
240→          })
241→      );
242→
243→      // Collect all warnings
244→      const aiIssues = await aiValidationPromise;
245→      const aiWarnings = aiIssues.map(
246→        (i) => `[AI] ${i.chapter}: ${i.message}`
247→      );
248→      const allWarnings = [
249→        ...texWarnings,
250→        ...preflightWarnings.map(
251→          (i) => `${i.chapter}${i.line ? `:${i.line}` : ""}: ${i.message}`
252→        ),
253→        ...aiWarnings,
254→        ...result.log.warnings.map(String),
255→      ];
256→
257→      // Warning budget: warn (non-blocking) when warnings exceed threshold
258→      const WARNING_BUDGET = 20;
259→      const warningBudgetExceeded = allWarnings.length > WARNING_BUDGET;
260→
261→      if (result.success && result.pdfPath) {
262→        // Upload compiled PDF to R2
263→        let pdfUrl = result.pdfPath;
264→        try {
265→          const pdfBytes = await readFile(result.pdfPath);
266→          const r2Key = `projects/${id}/compilations/${compilation.id}.pdf`;
267→          await uploadToR2(r2Key, pdfBytes, "application/pdf");
268→          pdfUrl = r2Key;
269→        } catch (r2Err) {
270→          console.warn("Failed to upload PDF to R2, storing local path:", r2Err);
271→          // Keep the local path as fallback
272→        }
273→
274→        await updateCompilation(supabase, compilation.id, {
275→          status: "completed",
276→          warnings: allWarnings,
277→          errors: [],
278→          pdf_url: pdfUrl,
279→          log_text: result.rawLog.slice(0, 50000),
280→          compile_time_ms: result.compileTimeMs,
281→        });
282→
283→        return NextResponse.json({
284→          data: {
285→            compilation_id: compilation.id,
286→            status: "completed",
287→            warnings: allWarnings,
288→            warning_budget_exceeded: warningBudgetExceeded,
289→            errors: [],
290→            compile_time_ms: result.compileTimeMs,
291→          },
292→        });
293→      }
294→
295→      // Compilation failed
296→      await updateCompilation(supabase, compilation.id, {
297→        status: "failed",
298→        warnings: allWarnings,
299→        errors: result.log.errors,
300→        log_text: result.rawLog.slice(0, 50000),
301→        compile_time_ms: result.compileTimeMs,
302→      });
303→
304→      return NextResponse.json(
305→        {
306→          data: {
307→            compilation_id: compilation.id,
308→            status: "failed",
309→            warnings: allWarnings,
310→            errors: result.log.errors,
311→            compile_time_ms: result.compileTimeMs,
312→          },
313→        },
314→        { status: 422 }
315→      );
316→    } finally {
317→      await release(slot.jobId!);
318→    }
319→  } catch (err) {
320→    console.error("Unexpected error in POST /api/projects/[id]/compile:", err);
321→    return internalError();
322→  }
323→}
324→
325→type WatermarkMode = "sandbox" | "draft_footer" | "none";
326→
327→function getWatermarkMode(status: string, currentPhase: number): WatermarkMode {
328→  if (status === "sandbox") return "sandbox";
329→  if (status === "completed") return "none";
330→  // Licensed project: draft footer from Phase 6 onwards
331→  if (status === "licensed" && currentPhase >= 6) return "draft_footer";
332→  return "none";
333→}
334→
335→async function updateCompilation(
336→  supabase: ReturnType<typeof createAdminSupabaseClient>,
337→  compilationId: string,
338→  data: Partial<Compilation>
339→) {
340→  await supabase
341→    .from("compilations")
342→    .update(data)
343→    .eq("id", compilationId);
344→}
