 1→import { NextRequest } from "next/server";
 2→import { readFile } from "fs/promises";
 3→import path from "path";
 4→import os from "os";
 5→import { getAuthenticatedUser } from "@/lib/api/auth";
 6→import { unauthorised, notFound, internalError } from "@/lib/api/errors";
 7→import { createAdminSupabaseClient } from "@/lib/supabase/admin";
 8→import { generateDownloadUrl } from "@/lib/r2/client";
 9→
10→export async function GET(
11→  request: NextRequest,
12→  { params }: { params: Promise<{ id: string }> }
13→) {
14→  try {
15→    const authResult = await getAuthenticatedUser();
16→    if (!authResult) return unauthorised();
17→
18→    const { id } = await params;
19→    const supabase = createAdminSupabaseClient();
20→
21→    // Verify project belongs to user
22→    const { data: project, error: projectError } = await supabase
23→      .from("projects")
24→      .select("id")
25→      .eq("id", id)
26→      .eq("user_id", authResult.user.id)
27→      .single();
28→
29→    if (projectError || !project) {
30→      return notFound("Project not found");
31→    }
32→
33→    // Get latest successful compilation
34→    const { data: compilation, error } = await supabase
35→      .from("compilations")
36→      .select("pdf_url")
37→      .eq("project_id", id)
38→      .eq("status", "completed")
39→      .order("created_at", { ascending: false })
40→      .limit(1)
41→      .single();
42→
43→    if (error || !compilation?.pdf_url) {
44→      return notFound("No compiled PDF available --- trigger a compilation first");
45→    }
46→
47→    const download = request.nextUrl.searchParams.get("download") === "1";
48→    const disposition = download
49→      ? `attachment; filename="thesis.pdf"`
50→      : "inline";
51→
52→    // If pdf_url is an R2 key (starts with "projects/"), redirect to signed URL
53→    if (compilation.pdf_url.startsWith("projects/")) {
54→      try {
55→        const signedUrl = await generateDownloadUrl(compilation.pdf_url);
56→        return Response.redirect(signedUrl, 302);
57→      } catch {
58→        return notFound("PDF file not found in storage --- recompile to regenerate");
59→      }
60→    }
61→
62→    // Fallback: local disk path (dev/test or legacy compilations)
63→    let pdfBytes: Uint8Array | null = null;
64→    for (const candidate of [
65→      compilation.pdf_url,
66→      path.join(os.tmpdir(), "apollo-pdfs", `${id}.pdf`),
67→    ]) {
68→      try {
69→        pdfBytes = await readFile(candidate);
70→        break;
71→      } catch {
72→        // Try next candidate
73→      }
74→    }
75→
76→    if (!pdfBytes) {
77→      return notFound("PDF file not found on disk --- recompile to regenerate");
78→    }
79→
80→    return new Response(pdfBytes as unknown as BodyInit, {
81→      headers: {
82→        "Content-Type": "application/pdf",
83→        "Content-Disposition": disposition,
84→        "Cache-Control": "private, max-age=60",
85→      },
86→    });
87→  } catch (err) {
88→    console.error("Unexpected error in GET /api/projects/[id]/preview.pdf:", err);
89→    return internalError();
90→  }
91→}
