 15→interface CompileResult {
 16→  compilation_id: string;
 17→  status: string;
 18→  warnings: string[];
 19→  compile_time_ms: number;
 20→}
 21→
 22→export function CompileButton({ projectId, disabled, onCompileSuccess, onResult, onError }: CompileButtonProps) {
 23→  const [isCompiling, setIsCompiling] = useState(false);
 24→  const [queuePosition, setQueuePosition] = useState<number | null>(null);
 25→  const [estimatedWait, setEstimatedWait] = useState<number | null>(null);
 26→  const pollRef = useRef<ReturnType<typeof setInterval> | null>(null);
 27→
 28→  // Poll for queue position when queued
 29→  useEffect(() => {
 30→    if (queuePosition === null || queuePosition <= 0) {
 31→      if (pollRef.current) {
 32→        clearInterval(pollRef.current);
 33→        pollRef.current = null;
 34→      }
 35→      return;
 36→    }
 37→
 38→    pollRef.current = setInterval(async () => {
 39→      try {
 40→        const response = await fetch(`/api/projects/${projectId}/compile`, {
 41→          method: "POST",
 42→        });
 43→        const body = await response.json();
 44→
 45→        if (response.status === 202 || response.status === 429) {
 46→          const pos = body.data?.queue_position ?? body.error?.queue_position;
 47→          const wait = body.data?.estimated_wait_ms ?? body.error?.estimated_wait_ms;
 48→          setQueuePosition(pos ?? null);
 49→          setEstimatedWait(wait ?? null);
 50→        } else if (response.ok && body.data?.status === "completed") {
 51→          const r = {
 52→            compilation_id: body.data.compilation_id,
 53→            status: "completed",
 54→            warnings: Array.isArray(body.data.warnings) ? body.data.warnings : [],
 55→            compile_time_ms: body.data.compile_time_ms ?? 0,
 56→          };
 57→          onResult?.(r);
 58→          setQueuePosition(null);
 59→          setEstimatedWait(null);
 60→          setIsCompiling(false);
 61→          onCompileSuccess?.();
 62→        } else if (body.error) {
 63→          onError?.(body.error.message);
 64→          setQueuePosition(null);
 65→          setIsCompiling(false);
 66→        }
 67→      } catch {
 68→        // Ignore polling errors
 69→      }
 70→    }, 5000);
 71→
 72→    return () => {
 73→      if (pollRef.current) clearInterval(pollRef.current);
 74→    };
 75→  }, [queuePosition, projectId, onCompileSuccess, onResult, onError]);
 76→
 77→  async function handleCompile() {
 78→    setIsCompiling(true);
 79→    onResult?.(null);
 80→    onError?.(null);
 81→    setQueuePosition(null);
 82→    setEstimatedWait(null);
 83→
 84→    try {
 85→      const response = await fetch(`/api/projects/${projectId}/compile`, {
 86→        method: "POST",
 87→      });
 88→
 89→      const body = await response.json();
 90→
 91→      if (response.status === 202 || response.status === 429) {
 92→        // Queued — extract position and start polling
 93→        setQueuePosition(body.error?.queue_position ?? body.data?.queue_position ?? 1);
 94→        setEstimatedWait(body.error?.estimated_wait_ms ?? body.data?.estimated_wait_ms ?? null);
 95→        return; // Keep polling
 96→      }
 97→
 98→      const data = body.data;
 99→
100→      if (response.ok && data?.status === "completed") {
101→        // Success — store normalised result
102→        const r = {
103→          compilation_id: data.compilation_id,
104→          status: "completed",
105→          warnings: Array.isArray(data.warnings) ? data.warnings : [],
106→          compile_time_ms: data.compile_time_ms ?? 0,
107→        };
108→        onResult?.(r);
109→        onCompileSuccess?.();
110→      } else if (data?.status === "validation_failed") {
111→        // Pre-flight validation failed — show issues as error text
112→        const issues: { chapter: string; message: string }[] = Array.isArray(data.issues) ? data.issues : [];
113→        const msg = issues.length > 0
114→          ? `Validation failed: ${issues.map((i: { chapter: string; message: string }) => `${i.chapter} — ${i.message}`).join("; ")}`
115→          : "Validation failed — fix issues in the editor and retry";
116→        onError?.(msg);
117→      } else if (data?.status === "failed") {
118→        // Compile failed — show errors
119→        const errors: string[] = Array.isArray(data.errors) ? data.errors : [];
120→        const msg = errors.length > 0
121→          ? `Compilation failed: ${errors.join("; ")}`
122→          : "Compilation failed — check the LaTeX source for errors";
123→        onError?.(msg);
124→      } else if (body.error?.message) {
125→        onError?.(body.error.message);
126→      } else {
127→        onError?.("Unexpected response from server");
128→      }
129→    } catch (err) {
130→      const msg = err instanceof Error ? err.message : "Compilation failed";
131→      onError?.(msg);
132→    } finally {
133→      if (queuePosition === null) {
134→        setIsCompiling(false);
135→      }
136→    }
137→  }
138→
139→  return (
140→    <div className="flex items-center gap-2">
141→      <Button
142→        onClick={handleCompile}
143→        disabled={disabled || isCompiling}
144→        variant="outline"
145→        size="sm"
146→      >
147→        {isCompiling ? (
148→          <>
149→            <Loader2 className="h-4 w-4 animate-spin" />
150→            {queuePosition ? "Queued..." : "Compiling..."}
151→          </>
152→        ) : (
153→          <>
154→            <FileText className="h-4 w-4" />
155→            Compile PDF
156→          </>
157→        )}
158→      </Button>
159→
160→      {/* Queue position badge */}
161→      {queuePosition !== null && queuePosition > 0 && (
162→        <span className="inline-flex items-center gap-1 rounded-full bg-[#D4A373]/10 px-2.5 py-1 text-xs font-medium text-[#B8885A]">
163→          <Clock className="h-3 w-3" />
164→          {queuePosition} ahead
165→          {estimatedWait !== null && (
166→            <> — est. {Math.ceil(estimatedWait / 1000)}s</>
167→          )}
168→        </span>
169→      )}
170→    </div>
171→  );
172→}
