"use client";

import { useState, useEffect } from "react";
import Link from "next/link";
import { ArrowRight } from "lucide-react";
import { motion, useReducedMotion } from "motion/react";

// ── TypewriterText ──────────────────────────────────────────────────────────
function TypewriterText({
  text,
  charDelay = 50,
  startDelay = 1200,
  reducedMotion = false,
}: {
  text: string;
  charDelay?: number;
  startDelay?: number;
  reducedMotion?: boolean;
}) {
  const [displayed, setDisplayed] = useState(reducedMotion ? text : "");
  const [showCursor, setShowCursor] = useState(!reducedMotion);

  useEffect(() => {
    if (reducedMotion) return;
    const startTimeout = setTimeout(() => {
      let i = 0;
      const interval = setInterval(() => {
        i++;
        setDisplayed(text.slice(0, i));
        if (i >= text.length) {
          clearInterval(interval);
          setTimeout(() => setShowCursor(false), 2000);
        }
      }, charDelay);
      return () => clearInterval(interval);
    }, startDelay);
    return () => clearTimeout(startTimeout);
  }, [text, charDelay, startDelay, reducedMotion]);

  return (
    <span>
      {displayed}
      {showCursor && (
        <span className="animate-typewriter-cursor">|</span>
      )}
    </span>
  );
}

// ── Fake text lines helper ──────────────────────────────────────────────────
function TextLines({ widths }: { widths: string[] }) {
  return (
    <div className="space-y-[5px]">
      {widths.map((w, i) => (
        <div
          key={i}
          className="h-[2px] rounded-full bg-[#D4D4D4]"
          style={{ width: w }}
        />
      ))}
    </div>
  );
}

// ── HeroSection ─────────────────────────────────────────────────────────────
export function HeroSection() {
  const prefersReducedMotion = useReducedMotion();

  const dur = prefersReducedMotion ? 0 : 1.0;
  const zenEase = [0.16, 1, 0.3, 1] as const;

  return (
    <section className="relative min-h-[90vh] overflow-hidden pt-32">
      <div className="container">
        <div className="grid grid-cols-1 place-items-center gap-12 lg:grid-cols-12 lg:gap-8">
          {/* ── Left zone ──────────────────────────────────────────────── */}
          <div className="lg:col-span-5">
            <motion.h1
              className="font-serif text-[80px] font-normal leading-[0.95] tracking-[-0.03em] text-[#2F2F2F] md:text-[140px]"
              initial={{ opacity: 0, y: 60, scale: 0.95 }}
              animate={{ opacity: 1, y: 0, scale: 1 }}
              transition={{ duration: dur, delay: 0.2, ease: zenEase }}
            >
              Apollo
            </motion.h1>

            <motion.p
              className="mt-8 max-w-md font-typewriter text-[17px] leading-relaxed text-[#6B6B6B] md:text-[19px]"
              initial={{ opacity: 0, y: 40 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: prefersReducedMotion ? 0 : 0.8, delay: 0.4, ease: zenEase }}
            >
              <TypewriterText
                text="From synopsis to submission — structured, cited, compliant."
                reducedMotion={prefersReducedMotion ?? false}
              />
            </motion.p>

            <motion.div
              className="mt-10 flex items-center gap-4"
              initial={{ opacity: 0, y: 40 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: prefersReducedMotion ? 0 : 0.8, delay: 0.6, ease: zenEase }}
            >
              <Link
                href="/sign-up"
                className="inline-flex items-center gap-2 rounded-full bg-[#2F2F2F] px-6 py-3 text-[15px] font-medium text-white transition-colors hover:bg-[#2F2F2F]/90"
              >
                Get Started Free
                <ArrowRight className="h-4 w-4" />
              </Link>
              <a
                href="#features"
                className="inline-flex items-center rounded-full border border-[rgba(0,0,0,0.06)] px-6 py-3 text-[15px] font-medium text-[#2F2F2F] transition-colors hover:bg-black/[0.04]"
              >
                Learn More
              </a>
            </motion.div>
          </div>

          {/* ── Right zone — unified dramatic composition ─────────────── */}
          <div className="relative hidden h-[540px] w-full lg:col-span-7 lg:block">
            {/*
             * Everything is positioned absolutely inside this container.
             * The three layers — pages, spheres, chart — OVERLAP to form
             * one cohesive visual cluster centred in the container.
             */}

            {/* ── Page 1 (back-left, largest tilt) ───────────────────── */}
            <motion.div
              className="absolute paper-shadow animate-float-thesis"
              style={{
                left: "2%",
                top: "8%",
                width: 260,
                transform: "rotate(-14deg)",
                animationDelay: "0s",
                zIndex: 1,
              }}
              initial={{ opacity: 0, y: 40, scale: 0.9 }}
              animate={{ opacity: 1, y: 0, scale: 1 }}
              transition={{ duration: dur, delay: 0.5, ease: zenEase }}
            >
              <div className="rounded-xl bg-white p-6">
                <div className="mb-4 h-[3px] w-[55%] rounded-full bg-[#C0C0C0]" />
                <TextLines widths={["88%","100%","72%","95%","60%","100%","78%","52%","90%","68%","100%","55%"]} />
              </div>
            </motion.div>

            {/* ── Page 2 (centre, slight tilt, page curl) ────────────── */}
            <motion.div
              className="absolute paper-shadow animate-float-thesis"
              style={{
                left: "14%",
                top: "2%",
                width: 280,
                transform: "rotate(-6deg)",
                animationDelay: "1.2s",
                zIndex: 3,
              }}
              initial={{ opacity: 0, y: 40, scale: 0.9 }}
              animate={{ opacity: 1, y: 0, scale: 1 }}
              transition={{ duration: dur, delay: 0.6, ease: zenEase }}
            >
              <div className="relative rounded-xl bg-white p-6">
                <div className="mb-4 h-[3px] w-[50%] rounded-full bg-[#C0C0C0]" />
                <TextLines widths={["82%","100%","66%","95%","55%","90%","74%","100%","48%","85%","100%","60%"]} />
                {/* Page curl */}
                <div className="absolute bottom-0 right-0 h-10 w-10 rounded-tl-2xl bg-gradient-to-tl from-[#E8E8E8] to-transparent" />
              </div>
            </motion.div>

            {/* ── Page 3 (front-right, opposite tilt) ────────────────── */}
            <motion.div
              className="absolute paper-shadow animate-float-thesis"
              style={{
                left: "30%",
                top: "14%",
                width: 240,
                transform: "rotate(4deg)",
                animationDelay: "2.4s",
                zIndex: 2,
              }}
              initial={{ opacity: 0, y: 40, scale: 0.9 }}
              animate={{ opacity: 1, y: 0, scale: 1 }}
              transition={{ duration: dur, delay: 0.7, ease: zenEase }}
            >
              <div className="rounded-xl bg-white p-6">
                <div className="mb-4 h-[3px] w-[62%] rounded-full bg-[#C0C0C0]" />
                <TextLines widths={["92%","100%","70%","88%","58%","100%","75%","46%"]} />
              </div>
            </motion.div>

            {/* ── Glass Spheres — overlapping cluster on the right ──── */}
            <motion.div
              className="absolute"
              style={{ right: "0%", top: "4%", zIndex: 5 }}
              initial={{ opacity: 0, scale: 0.7 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ duration: dur, delay: 0.85, ease: zenEase }}
            >
              <div className="relative h-[220px] w-[200px]">
                {/* Large sage */}
                <div
                  className="absolute rounded-full bg-gradient-to-br from-[#8B9D77]/90 to-[#6B7D57]/70 backdrop-blur-[12px] border border-white/30 shadow-[inset_0_1px_2px_rgba(255,255,255,0.4)] animate-float-sphere"
                  style={{ width: 80, height: 80, top: 0, left: 20, animationDelay: "0s" }}
                />
                {/* Medium amber */}
                <div
                  className="absolute rounded-full bg-gradient-to-br from-[#D4A373]/80 to-[#B8885A]/60 backdrop-blur-[12px] border border-white/25 shadow-[inset_0_1px_2px_rgba(255,255,255,0.3)] animate-float-sphere"
                  style={{ width: 58, height: 58, top: 50, left: 90, animationDelay: "0.8s" }}
                />
                {/* Large cream */}
                <div
                  className="absolute rounded-full bg-gradient-to-br from-[#F0E6D6]/85 to-[#E0D0B8]/65 backdrop-blur-[10px] border border-white/30 shadow-[inset_0_1px_2px_rgba(255,255,255,0.4)] animate-float-sphere"
                  style={{ width: 64, height: 64, top: 85, left: 10, animationDelay: "1.4s" }}
                />
                {/* Medium sage-2 */}
                <div
                  className="absolute rounded-full bg-gradient-to-br from-[#8B9D77]/65 to-[#A8B894]/45 backdrop-blur-[10px] border border-white/25 animate-float-sphere"
                  style={{ width: 50, height: 50, top: 20, left: 100, animationDelay: "1.2s" }}
                />
                {/* Small charcoal */}
                <div
                  className="absolute rounded-full bg-gradient-to-br from-[#2F2F2F]/55 to-[#4A4A4A]/35 border border-white/15 animate-float-sphere"
                  style={{ width: 32, height: 32, top: 120, left: 80, animationDelay: "2s" }}
                />
                {/* Tiny amber-2 */}
                <div
                  className="absolute rounded-full bg-gradient-to-br from-[#D4A373]/60 to-[#C4935A]/40 border border-white/20 animate-float-sphere"
                  style={{ width: 26, height: 26, top: 60, left: 0, animationDelay: "2.4s" }}
                />
                {/* Medium cream-2 */}
                <div
                  className="absolute rounded-full bg-gradient-to-br from-[#E8DCC8]/80 to-[#D4C4A8]/55 backdrop-blur-[8px] border border-white/25 animate-float-sphere"
                  style={{ width: 44, height: 44, top: 140, left: 55, animationDelay: "0.6s" }}
                />
                {/* Small sage-3 */}
                <div
                  className="absolute rounded-full bg-gradient-to-br from-[#A8B894]/70 to-[#8B9D77]/45 border border-white/20 animate-float-sphere"
                  style={{ width: 36, height: 36, top: 100, left: 140, animationDelay: "1.6s" }}
                />
                {/* Tiny cream-3 */}
                <div
                  className="absolute rounded-full bg-gradient-to-br from-[#F0E6D6]/60 to-[#E0D0B8]/40 border border-white/20 animate-float-sphere"
                  style={{ width: 22, height: 22, top: 170, left: 100, animationDelay: "1.8s" }}
                />
                {/* Extra sage highlight */}
                <div
                  className="absolute rounded-full bg-gradient-to-br from-[#8B9D77]/50 to-[#6B7D57]/30 backdrop-blur-[6px] border border-white/20 animate-float-sphere"
                  style={{ width: 40, height: 40, top: 160, left: 25, animationDelay: "0.4s" }}
                />
              </div>
            </motion.div>

            {/* ── Glass Chart — overlapping lower-right of pages ─────── */}
            <motion.div
              className="absolute animate-float-chart"
              style={{ right: "6%", bottom: "4%", zIndex: 4 }}
              initial={{ opacity: 0, y: 30, scale: 0.85 }}
              animate={{ opacity: 1, y: 0, scale: 1 }}
              transition={{ duration: dur, delay: 1.0, ease: zenEase }}
            >
              <div
                className="w-[240px] rounded-2xl border border-white/50 bg-white/50 p-5 shadow-[0_8px_40px_rgba(0,0,0,0.10)]"
                style={{ backdropFilter: "blur(20px) saturate(180%)", WebkitBackdropFilter: "blur(20px) saturate(180%)" }}
              >
                <svg viewBox="0 0 180 100" className="w-full">
                  <defs>
                    <linearGradient id="heroChartFill" x1="0" y1="0" x2="0" y2="1">
                      <stop offset="0%" stopColor="#8B9D77" stopOpacity="0.35" />
                      <stop offset="100%" stopColor="#8B9D77" stopOpacity="0.03" />
                    </linearGradient>
                  </defs>
                  {/* Grid lines */}
                  <line x1="15" y1="25" x2="170" y2="25" stroke="#E0E0E0" strokeWidth="0.5" />
                  <line x1="15" y1="55" x2="170" y2="55" stroke="#E0E0E0" strokeWidth="0.5" />
                  <line x1="15" y1="85" x2="170" y2="85" stroke="#E0E0E0" strokeWidth="0.5" />
                  {/* Filled area */}
                  <polygon
                    fill="url(#heroChartFill)"
                    points="20,72 45,58 70,62 95,38 120,42 145,28 165,32 165,85 20,85"
                  />
                  {/* Data line */}
                  <polyline
                    fill="none"
                    stroke="#8B9D77"
                    strokeWidth="2.5"
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    points="20,72 45,58 70,62 95,38 120,42 145,28 165,32"
                  />
                  {/* Data points */}
                  {[
                    [20,72],[45,58],[70,62],[95,38],[120,42],[145,28],[165,32]
                  ].map(([cx,cy], i) => (
                    <circle key={i} cx={cx} cy={cy} r="3.5" fill="#8B9D77" />
                  ))}
                </svg>
              </div>
            </motion.div>
          </div>
        </div>
      </div>
    </section>
  );
}

