  border-radius: 0 0 3px 0;
  box-shadow: -3px -3px 8px rgba(0, 0, 0, 0.06);
}

.paper-curl-bottom {
  position: relative;
}
.paper-curl-bottom::after {
  content: '';
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  height: 8px;
  background: linear-gradient(to bottom, transparent, rgba(0, 0, 0, 0.04));
  border-radius: 0 0 3px 3px;
}

.paper-curl-side {
  position: relative;
}
.paper-curl-side::after {
  content: '';
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  width: 8px;
  background: linear-gradient(to right, transparent, rgba(0, 0, 0, 0.05));
  border-radius: 0 3px 3px 0;
}

