                <ThesisPage
                  curlVariant="side"
                  titleWidth="55%"
                  paragraphs={PAGE_C_PARAGRAPHS}
                />
              </motion.div>
            </motion.div>

            {/* ── Glass Spheres — tight cluster on the right ─────────── */}
            <motion.div
              className="absolute"
              style={{ right: "-2%", top: "3%", zIndex: 4 }}
              initial={{ opacity: 0, scale: 0.7 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ duration: dur, delay: 0.9, ease: zenEase }}
            >
              <div className="relative h-[240px] w-[200px]">
                {SPHERES.map((s, i) => (
                  <GlassSphere
                    key={i}
                    size={s.size}
                    top={s.top}
                    left={s.left}
                    baseColor={s.baseColor}
                    darkColor={s.darkColor}
                    delay={s.delay}
                  />
                ))}
              </div>
            </motion.div>
          </div>
        </div>
      </div>
    </section>
  );
}

