"use client";

import { useRef, useMemo, useEffect, useState } from "react";
import { Canvas, useFrame } from "@react-three/fiber";
import { Float, ContactShadows, Line } from "@react-three/drei";
import * as THREE from "three";

// ── Reduced motion detection ────────────────────────────────────────────────
function useReducedMotion() {
  const [reduced, setReduced] = useState(false);

  useEffect(() => {
    const mq = window.matchMedia("(prefers-reduced-motion: reduce)");
    setReduced(mq.matches);
    const handler = (e: MediaQueryListEvent) => setReduced(e.matches);
    mq.addEventListener("change", handler);
    return () => mq.removeEventListener("change", handler);
  }, []);

  return reduced;
}

// ── Canvas2D page texture generator ─────────────────────────────────────────
function createPageTexture(variant: 0 | 1 | 2): THREE.CanvasTexture {
  const w = 512;
  const h = 720;
  const canvas = document.createElement("canvas");
  canvas.width = w;
  canvas.height = h;
  const ctx = canvas.getContext("2d")!;

  // Paper background
  ctx.fillStyle = "#FDFCFA";
  ctx.fillRect(0, 0, w, h);

  // Subtle grain
  for (let i = 0; i < 800; i++) {
    const gx = Math.floor(((i * 7919 + 104729) % 100003) / 100003 * w);
    const gy = Math.floor(((i * 6271 + 73856) % 100003) / 100003 * h);
    ctx.fillStyle = `rgba(0,0,0,${0.01 + (i % 3) * 0.005})`;
    ctx.fillRect(gx, gy, 1, 1);
  }

  const margin = 50;
  const contentW = w - margin * 2;

  // Title line
  ctx.fillStyle = "#B0B0B0";
  const titleWidths = [0.5, 0.45, 0.55];
  roundRect(ctx, margin, 50, contentW * titleWidths[variant], 5, 2.5);

  // Paragraph blocks
  const paragraphs = [
    [[0.88, 1, 0.72, 0.95], [0.6, 1, 0.78, 0.52], [0.9, 0.68, 1, 0.55]],
    [[0.82, 1, 0.66, 0.95, 0.55], [0.9, 0.74, 1, 0.48, 0.85], [1, 0.6, 0.92, 0.7, 0.44]],
    [[0.92, 1, 0.7, 0.88], [0.58, 1, 0.75, 0.46]],
  ];

  ctx.fillStyle = "#C8C8C8";
  let y = 85;
  for (const block of paragraphs[variant]) {
    for (const lineW of block) {
      roundRect(ctx, margin, y, contentW * lineW, 3, 1.5);
      y += 10;
    }
    y += 18;
  }

  const texture = new THREE.CanvasTexture(canvas);
  texture.minFilter = THREE.LinearFilter;
  texture.magFilter = THREE.LinearFilter;
  return texture;
}

function roundRect(
  ctx: CanvasRenderingContext2D,
  x: number,
  y: number,
  w: number,
  h: number,
  r: number,
) {
  ctx.beginPath();
  ctx.roundRect(x, y, w, h, r);
  ctx.fill();
}

// ── Thesis Page mesh ────────────────────────────────────────────────────────
function ThesisPageMesh({ variant }: { variant: 0 | 1 | 2 }) {
  const texture = useMemo(() => createPageTexture(variant), [variant]);

  useEffect(() => {
    return () => { texture.dispose(); };
  }, [texture]);

  return (
    <mesh>
      <planeGeometry args={[2.1, 2.97]} />
      <meshStandardMaterial
        map={texture}
        side={THREE.FrontSide}
        roughness={0.85}
        metalness={0}
      />
    </mesh>
  );
}

// ── Thesis pages group ──────────────────────────────────────────────────────
function ThesisPagesGroup({ reducedMotion }: { reducedMotion: boolean }) {
  const Wrapper = reducedMotion ? StaticGroup : FloatWrapper;

  return (
    <group position={[-1.5, 0.3, 0]}>
      {/* Page A — back-left */}
      <Wrapper speed={1.2} floatIntensity={0.4} rotationIntensity={0.15}>
        <group rotation={[0, -0.26, 0]} position={[-0.3, 0.2, -0.5]}>
          <ThesisPageMesh variant={0} />
        </group>
      </Wrapper>

      {/* Page B — centre-front (on top) */}
      <Wrapper speed={1.0} floatIntensity={0.35} rotationIntensity={0.1}>
        <group rotation={[0.03, -0.1, 0]} position={[0.2, 0.4, 0.3]}>
          <ThesisPageMesh variant={1} />
        </group>
      </Wrapper>

      {/* Page C — right-lean */}
      <Wrapper speed={0.8} floatIntensity={0.3} rotationIntensity={0.12}>
        <group rotation={[0, 0.09, 0]} position={[0.8, 0, -0.2]}>
          <ThesisPageMesh variant={2} />
        </group>
      </Wrapper>
    </group>
  );
}

// ── Float / Static wrapper ──────────────────────────────────────────────────
function FloatWrapper({
  children,
  speed,
  floatIntensity,
  rotationIntensity,
}: {
  children: React.ReactNode;
  speed: number;
  floatIntensity: number;
  rotationIntensity: number;
}) {
  return (
    <Float speed={speed} floatIntensity={floatIntensity} rotationIntensity={rotationIntensity}>
      {children}
    </Float>
  );
}

function StaticGroup({
  children,
}: {
  children: React.ReactNode;
  speed?: number;
  floatIntensity?: number;
  rotationIntensity?: number;
}) {
  return <group>{children}</group>;
}

// ── Scatter data — 18 deterministic points ──────────────────────────────────
const SCATTER_COLORS = ["#8B9D77", "#D4A373", "#E8DCC8", "#6B7A5E"];

const SCATTER_POINTS: { x: number; y: number; z: number; color: string }[] = [
  { x: 0.2, y: 0.35, z: 0.15, color: SCATTER_COLORS[0] },
  { x: 0.5, y: 0.58, z: 0.22, color: SCATTER_COLORS[1] },
  { x: 0.8, y: 0.72, z: 0.35, color: SCATTER_COLORS[2] },
  { x: 1.1, y: 1.05, z: 0.18, color: SCATTER_COLORS[3] },
  { x: 1.4, y: 1.12, z: 0.42, color: SCATTER_COLORS[0] },
  { x: 0.3, y: 0.48, z: 0.28, color: SCATTER_COLORS[1] },
  { x: 0.7, y: 0.62, z: 0.12, color: SCATTER_COLORS[2] },
  { x: 1.0, y: 0.88, z: 0.38, color: SCATTER_COLORS[3] },
  { x: 1.3, y: 1.18, z: 0.25, color: SCATTER_COLORS[0] },
  { x: 1.6, y: 1.32, z: 0.32, color: SCATTER_COLORS[1] },
  { x: 0.4, y: 0.42, z: 0.08, color: SCATTER_COLORS[2] },
  { x: 0.9, y: 0.82, z: 0.45, color: SCATTER_COLORS[3] },
  { x: 1.2, y: 0.95, z: 0.15, color: SCATTER_COLORS[0] },
  { x: 1.5, y: 1.28, z: 0.38, color: SCATTER_COLORS[1] },
  { x: 1.7, y: 1.45, z: 0.20, color: SCATTER_COLORS[2] },
  { x: 0.6, y: 0.55, z: 0.30, color: SCATTER_COLORS[3] },
  { x: 1.8, y: 1.52, z: 0.42, color: SCATTER_COLORS[0] },
  { x: 0.15, y: 0.28, z: 0.35, color: SCATTER_COLORS[1] },
];

// ── Scatter sphere ──────────────────────────────────────────────────────────
const sphereGeometry = new THREE.SphereGeometry(0.06, 16, 16);

function ScatterSphere({
  position,
  color,
  reducedMotion,
  index,
}: {
  position: [number, number, number];
  color: string;
  reducedMotion: boolean;
  index: number;
}) {
  const Wrapper = reducedMotion ? StaticGroup : FloatWrapper;

  return (
    <Wrapper
      speed={0.6 + (index % 4) * 0.2}
      floatIntensity={0.08}
      rotationIntensity={0}
    >
      <mesh geometry={sphereGeometry} position={position}>
        <meshPhysicalMaterial
          color={color}
          metalness={0.1}
          roughness={0.2}
          clearcoat={1.0}
          clearcoatRoughness={0.1}
        />
      </mesh>
    </Wrapper>
  );
}

// ── Axes with tick marks ────────────────────────────────────────────────────
const AXIS_COLOR = "#CCCCCC";
const AXIS_LENGTH = 2.2;
const TICK_SIZE = 0.05;
const TICK_INTERVAL = 0.5;

function ScatterAxes() {
  const ticks = useMemo(() => {
    const result: { start: [number, number, number]; end: [number, number, number] }[] = [];

    // X-axis ticks
    for (let t = TICK_INTERVAL; t <= AXIS_LENGTH; t += TICK_INTERVAL) {
      result.push({ start: [t, -TICK_SIZE, 0], end: [t, TICK_SIZE, 0] });
    }
    // Y-axis ticks
    for (let t = TICK_INTERVAL; t <= AXIS_LENGTH; t += TICK_INTERVAL) {
      result.push({ start: [-TICK_SIZE, t, 0], end: [TICK_SIZE, t, 0] });
    }
    // Z-axis ticks
    for (let t = TICK_INTERVAL; t <= 0.6; t += TICK_INTERVAL) {
      result.push({ start: [0, -TICK_SIZE, t], end: [0, TICK_SIZE, t] });
    }

    return result;
  }, []);

  return (
    <group>
      {/* X axis */}
      <Line
        points={[[0, 0, 0], [AXIS_LENGTH, 0, 0]]}
        color={AXIS_COLOR}
        lineWidth={1}
      />
      {/* Y axis */}
      <Line
        points={[[0, 0, 0], [0, AXIS_LENGTH, 0]]}
        color={AXIS_COLOR}
        lineWidth={1}
      />
      {/* Z axis */}
      <Line
        points={[[0, 0, 0], [0, 0, 0.6]]}
        color={AXIS_COLOR}
        lineWidth={1}
      />
      {/* Tick marks */}
      {ticks.map((tick, i) => (
        <Line
          key={i}
          points={[tick.start, tick.end]}
          color={AXIS_COLOR}
          lineWidth={1}
        />
      ))}
    </group>
  );
}

// ── Scatter plot group ──────────────────────────────────────────────────────
function ScatterPlotGroup({ reducedMotion }: { reducedMotion: boolean }) {
  return (
    <group position={[1.8, -0.5, 0]}>
      <ScatterAxes />
      {SCATTER_POINTS.map((pt, i) => (
        <ScatterSphere
          key={i}
          position={[pt.x, pt.y, pt.z]}
          color={pt.color}
          reducedMotion={reducedMotion}
          index={i}
        />
      ))}
    </group>
  );
}

// ── Slow scene rotation ─────────────────────────────────────────────────────
function SceneRotator({
  children,
  reducedMotion,
}: {
  children: React.ReactNode;
  reducedMotion: boolean;
}) {
  const groupRef = useRef<THREE.Group>(null);

  useFrame((_, delta) => {
    if (reducedMotion || !groupRef.current) return;
    groupRef.current.rotation.y += delta * 0.03;
  });

  return <group ref={groupRef}>{children}</group>;
}

// ── Main scene ──────────────────────────────────────────────────────────────
function Scene() {
  const reducedMotion = useReducedMotion();

  return (
    <>
      {/* Lighting */}
      <ambientLight intensity={0.5} />
      <directionalLight position={[5, 8, 5]} intensity={1.0} />
      <pointLight position={[-3, 3, 2]} intensity={0.3} color="#FFF5E6" />

      {/* Ground shadows */}
      <ContactShadows
        position={[0, -2.8, 0]}
        opacity={0.3}
        scale={12}
        blur={2}
        far={5}
        resolution={256}
      />

      <SceneRotator reducedMotion={reducedMotion}>
        <ThesisPagesGroup reducedMotion={reducedMotion} />
        <ScatterPlotGroup reducedMotion={reducedMotion} />
      </SceneRotator>
    </>
  );
}

// ── Exported component ──────────────────────────────────────────────────────
export function Hero3DScene() {
  return (
    <Canvas
      camera={{ position: [0, 1, 8], fov: 35, near: 0.1, far: 50 }}
      dpr={[1, 1.5]}
      gl={{ antialias: true, alpha: true, powerPreference: "low-power" }}
      style={{ background: "transparent" }}
    >
      <Scene />
    </Canvas>
  );
}

