"use client";

import { useRef, useMemo, useEffect, useState } from "react";
import { Canvas, useFrame } from "@react-three/fiber";
import { Float, ContactShadows, Line, Environment } from "@react-three/drei";
import * as THREE from "three";

// ── Reduced motion detection ────────────────────────────────────────────────
function useReducedMotion() {
  const [reduced, setReduced] = useState(false);

  useEffect(() => {
    const mq = window.matchMedia("(prefers-reduced-motion: reduce)");
    setReduced(mq.matches);
    const handler = (e: MediaQueryListEvent) => setReduced(e.matches);
    mq.addEventListener("change", handler);
    return () => mq.removeEventListener("change", handler);
  }, []);

  return reduced;
}

// ── Canvas2D page texture generator ─────────────────────────────────────────
function createPageTexture(variant: 0 | 1 | 2): THREE.CanvasTexture {
  const w = 512;
  const h = 720;
  const canvas = document.createElement("canvas");
  canvas.width = w;
  canvas.height = h;
  const ctx = canvas.getContext("2d")!;

  // Paper background — pure white so lighting tints it naturally
  ctx.fillStyle = "#FFFFFF";
  ctx.fillRect(0, 0, w, h);

  // Very subtle warm tint
  ctx.fillStyle = "rgba(253, 248, 240, 0.3)";
  ctx.fillRect(0, 0, w, h);

  // Subtle grain noise
  for (let i = 0; i < 600; i++) {
    const gx = Math.floor(((i * 7919 + 104729) % 100003) / 100003 * w);
    const gy = Math.floor(((i * 6271 + 73856) % 100003) / 100003 * h);
    ctx.fillStyle = `rgba(0,0,0,${0.008 + (i % 3) * 0.004})`;
    ctx.fillRect(gx, gy, 1, 1);
  }

  const margin = 50;
  const contentW = w - margin * 2;

  // Title line (darker for visibility)
  ctx.fillStyle = "#999999";
  const titleWidths = [0.5, 0.45, 0.55];
  roundRect(ctx, margin, 55, contentW * titleWidths[variant], 6, 3);

  // Paragraph blocks
  const paragraphs = [
    [[0.88, 1, 0.72, 0.95], [0.6, 1, 0.78, 0.52], [0.9, 0.68, 1, 0.55]],
    [[0.82, 1, 0.66, 0.95, 0.55], [0.9, 0.74, 1, 0.48, 0.85], [1, 0.6, 0.92, 0.7, 0.44]],
    [[0.92, 1, 0.7, 0.88], [0.58, 1, 0.75, 0.46], [0.85, 0.70, 1, 0.62]],
  ];

  ctx.fillStyle = "#BBBBBB";
  let y = 90;
  for (const block of paragraphs[variant]) {
    for (const lineW of block) {
      roundRect(ctx, margin, y, contentW * lineW, 3.5, 1.75);
      y += 12;
    }
    y += 22;
  }

  const texture = new THREE.CanvasTexture(canvas);
  texture.minFilter = THREE.LinearFilter;
  texture.magFilter = THREE.LinearFilter;
  texture.colorSpace = THREE.SRGBColorSpace;
  return texture;
}

function roundRect(
  ctx: CanvasRenderingContext2D,
  x: number,
  y: number,
  w: number,
  h: number,
  r: number,
) {
  ctx.beginPath();
  ctx.roundRect(x, y, w, h, r);
  ctx.fill();
}

// ── Thesis Page mesh ────────────────────────────────────────────────────────
function ThesisPageMesh({ variant }: { variant: 0 | 1 | 2 }) {
  const texture = useMemo(() => createPageTexture(variant), [variant]);

  useEffect(() => {
    return () => { texture.dispose(); };
  }, [texture]);

  return (
    <mesh castShadow>
      <planeGeometry args={[2.1, 2.97]} />
      <meshStandardMaterial
        map={texture}
        color="#FFFFFF"
        side={THREE.DoubleSide}
        roughness={0.9}
        metalness={0}
        emissive="#FFFFFF"
        emissiveIntensity={0.08}
      />
    </mesh>
  );
}

// ── Thesis pages group — fanned out with real 3D rotation ───────────────────
function ThesisPagesGroup({ reducedMotion }: { reducedMotion: boolean }) {
  const Wrapper = reducedMotion ? StaticGroup : FloatWrapper;

  return (
    <group position={[-1.2, 0.2, 0]}>
      {/* Page A — back-left, strongly rotated away */}
      <Wrapper speed={1.2} floatIntensity={0.3} rotationIntensity={0.08}>
        <group
          rotation={[0.06, -0.45, -0.03]}
          position={[-0.6, 0.15, -0.8]}
        >
          <ThesisPageMesh variant={0} />
        </group>
      </Wrapper>

      {/* Page B — centre-front, slight angle, overlapping */}
      <Wrapper speed={1.0} floatIntensity={0.25} rotationIntensity={0.06}>
        <group
          rotation={[0.04, -0.18, 0.02]}
          position={[0.1, 0.35, 0.2]}
        >
          <ThesisPageMesh variant={1} />
        </group>
      </Wrapper>

      {/* Page C — right, leaning the other way */}
      <Wrapper speed={0.8} floatIntensity={0.2} rotationIntensity={0.07}>
        <group
          rotation={[-0.03, 0.15, 0.04]}
          position={[0.7, -0.1, -0.3]}
        >
          <ThesisPageMesh variant={2} />
        </group>
      </Wrapper>
    </group>
  );
}

// ── Float / Static wrapper ──────────────────────────────────────────────────
function FloatWrapper({
  children,
  speed,
  floatIntensity,
  rotationIntensity,
}: {
  children: React.ReactNode;
  speed: number;
  floatIntensity: number;
  rotationIntensity: number;
}) {
  return (
    <Float speed={speed} floatIntensity={floatIntensity} rotationIntensity={rotationIntensity}>
      {children}
    </Float>
  );
}

function StaticGroup({
  children,
}: {
  children: React.ReactNode;
  speed?: number;
  floatIntensity?: number;
  rotationIntensity?: number;
}) {
  return <group>{children}</group>;
}

// ── Scatter data — 18 points with varying sizes + wider spread ──────────────
const SCATTER_COLORS = ["#8B9D77", "#D4A373", "#E8DCC8", "#6B7A5E"];

const SCATTER_POINTS: {
  x: number;
  y: number;
  z: number;
  radius: number;
  color: string;
}[] = [
  // Large spheres (prominent)
  { x: 0.15, y: 0.22, z: 0.30, radius: 0.12, color: SCATTER_COLORS[0] },
  { x: 1.65, y: 1.50, z: 0.15, radius: 0.11, color: SCATTER_COLORS[1] },
  { x: 0.90, y: 0.85, z: 0.40, radius: 0.10, color: SCATTER_COLORS[2] },
  // Medium-large spheres
  { x: 0.50, y: 0.52, z: 0.10, radius: 0.09, color: SCATTER_COLORS[3] },
  { x: 1.30, y: 1.20, z: 0.35, radius: 0.09, color: SCATTER_COLORS[0] },
  { x: 1.10, y: 0.78, z: 0.22, radius: 0.085, color: SCATTER_COLORS[1] },
  // Medium spheres
  { x: 0.35, y: 0.55, z: 0.45, radius: 0.075, color: SCATTER_COLORS[2] },
  { x: 0.75, y: 0.60, z: 0.08, radius: 0.07, color: SCATTER_COLORS[3] },
  { x: 1.45, y: 1.35, z: 0.28, radius: 0.07, color: SCATTER_COLORS[0] },
  { x: 1.80, y: 1.58, z: 0.38, radius: 0.075, color: SCATTER_COLORS[1] },
  // Small spheres (scattered outliers)
  { x: 0.60, y: 0.75, z: 0.50, radius: 0.055, color: SCATTER_COLORS[2] },
  { x: 1.20, y: 0.92, z: 0.05, radius: 0.05, color: SCATTER_COLORS[3] },
  { x: 0.25, y: 0.10, z: 0.18, radius: 0.05, color: SCATTER_COLORS[0] },
  { x: 1.55, y: 1.10, z: 0.48, radius: 0.055, color: SCATTER_COLORS[1] },
  // Tiny spheres (detail)
  { x: 0.45, y: 0.30, z: 0.35, radius: 0.04, color: SCATTER_COLORS[2] },
  { x: 0.85, y: 1.05, z: 0.55, radius: 0.04, color: SCATTER_COLORS[3] },
  { x: 1.70, y: 1.42, z: 0.10, radius: 0.035, color: SCATTER_COLORS[0] },
  { x: 1.00, y: 0.65, z: 0.42, radius: 0.038, color: SCATTER_COLORS[1] },
];

// ── Scatter sphere — per-sphere geometry with glass material ────────────────
function ScatterSphere({
  position,
  color,
  radius,
  reducedMotion,
  index,
}: {
  position: [number, number, number];
  color: string;
  radius: number;
  reducedMotion: boolean;
  index: number;
}) {
  const Wrapper = reducedMotion ? StaticGroup : FloatWrapper;
  const geometry = useMemo(() => new THREE.SphereGeometry(radius, 32, 32), [radius]);

  return (
    <Wrapper
      speed={0.5 + (index % 5) * 0.15}
      floatIntensity={0.06}
      rotationIntensity={0}
    >
      <mesh geometry={geometry} position={position} castShadow>
        <meshPhysicalMaterial
          color={color}
          metalness={0.05}
          roughness={0.05}
          clearcoat={1.0}
          clearcoatRoughness={0.05}
          transmission={0.4}
          thickness={radius * 8}
          ior={1.5}
          envMapIntensity={1.2}
        />
      </mesh>
    </Wrapper>
  );
}

// ── Axes with tick marks ────────────────────────────────────────────────────
const AXIS_COLOR = "#C0C0C0";
const AXIS_LENGTH = 2.2;
const TICK_SIZE = 0.05;
const TICK_INTERVAL = 0.5;

function ScatterAxes() {
  const ticks = useMemo(() => {
    const result: { start: [number, number, number]; end: [number, number, number] }[] = [];

    for (let t = TICK_INTERVAL; t <= AXIS_LENGTH; t += TICK_INTERVAL) {
      result.push({ start: [t, -TICK_SIZE, 0], end: [t, TICK_SIZE, 0] });
    }
    for (let t = TICK_INTERVAL; t <= AXIS_LENGTH; t += TICK_INTERVAL) {
      result.push({ start: [-TICK_SIZE, t, 0], end: [TICK_SIZE, t, 0] });
    }
    for (let t = TICK_INTERVAL; t <= 0.6; t += TICK_INTERVAL) {
      result.push({ start: [0, -TICK_SIZE, t], end: [0, TICK_SIZE, t] });
    }

    return result;
  }, []);

  return (
    <group>
      <Line points={[[0, 0, 0], [AXIS_LENGTH, 0, 0]]} color={AXIS_COLOR} lineWidth={1} />
      <Line points={[[0, 0, 0], [0, AXIS_LENGTH, 0]]} color={AXIS_COLOR} lineWidth={1} />
      <Line points={[[0, 0, 0], [0, 0, 0.6]]} color={AXIS_COLOR} lineWidth={1} />
      {ticks.map((tick, i) => (
        <Line key={i} points={[tick.start, tick.end]} color={AXIS_COLOR} lineWidth={1} />
      ))}
    </group>
  );
}

// ── Scatter plot group ──────────────────────────────────────────────────────
function ScatterPlotGroup({ reducedMotion }: { reducedMotion: boolean }) {
  return (
    <group position={[1.8, -0.5, 0]}>
      <ScatterAxes />
      {SCATTER_POINTS.map((pt, i) => (
        <ScatterSphere
          key={i}
          position={[pt.x, pt.y, pt.z]}
          color={pt.color}
          radius={pt.radius}
          reducedMotion={reducedMotion}
          index={i}
        />
      ))}
    </group>
  );
}

// ── Slow scene rotation ─────────────────────────────────────────────────────
function SceneRotator({
  children,
  reducedMotion,
}: {
  children: React.ReactNode;
  reducedMotion: boolean;
}) {
  const groupRef = useRef<THREE.Group>(null);

  useFrame((_, delta) => {
    if (reducedMotion || !groupRef.current) return;
    groupRef.current.rotation.y += delta * 0.025;
  });

  return <group ref={groupRef}>{children}</group>;
}

// ── Procedural environment (no HDR download) ────────────────────────────────
function StudioEnvironment() {
  return (
    <Environment frames={1} resolution={128} background={false}>
      {/* Warm top light */}
      <mesh position={[0, 6, 0]} scale={30}>
        <planeGeometry />
        <meshBasicMaterial color="#FFFEF8" />
      </mesh>
      {/* Cool bottom fill */}
      <mesh position={[0, -6, 0]} scale={30} rotation={[Math.PI, 0, 0]}>
        <planeGeometry />
        <meshBasicMaterial color="#E8E0D0" />
      </mesh>
      {/* Left warm fill */}
      <mesh position={[-8, 0, 0]} scale={20} rotation={[0, Math.PI / 2, 0]}>
        <planeGeometry />
        <meshBasicMaterial color="#FFF5E8" />
      </mesh>
      {/* Right cool fill */}
      <mesh position={[8, 0, 0]} scale={20} rotation={[0, -Math.PI / 2, 0]}>
        <planeGeometry />
        <meshBasicMaterial color="#E8ECF0" />
      </mesh>
      {/* Front highlight */}
      <mesh position={[0, 2, 8]} scale={15} rotation={[0, Math.PI, 0]}>
        <planeGeometry />
        <meshBasicMaterial color="#FFFFFF" />
      </mesh>
    </Environment>
  );
}

// ── Main scene ──────────────────────────────────────────────────────────────
function Scene() {
  const reducedMotion = useReducedMotion();

  return (
    <>
      {/* Procedural env map for glass reflections — no download */}
      <StudioEnvironment />

      {/* Lighting — bright enough for white paper */}
      <ambientLight intensity={1.0} />
      <directionalLight
        position={[5, 8, 5]}
        intensity={1.5}
        castShadow
        shadow-mapSize-width={512}
        shadow-mapSize-height={512}
      />
      <pointLight position={[-3, 3, 2]} intensity={0.4} color="#FFF5E6" />
      {/* Fill from below to soften shadows on pages */}
      <pointLight position={[0, -3, 4]} intensity={0.2} color="#F5F0E8" />

      {/* Ground contact shadows */}
      <ContactShadows
        position={[0, -2.2, 0]}
        opacity={0.35}
        scale={14}
        blur={2.5}
        far={6}
        resolution={256}
      />

      <SceneRotator reducedMotion={reducedMotion}>
        <ThesisPagesGroup reducedMotion={reducedMotion} />
        <ScatterPlotGroup reducedMotion={reducedMotion} />
      </SceneRotator>
    </>
  );
}

// ── Exported component ──────────────────────────────────────────────────────
export function Hero3DScene() {
  return (
    <Canvas
      camera={{ position: [0, 1, 8], fov: 35, near: 0.1, far: 50 }}
      dpr={[1, 1.5]}
      gl={{ antialias: true, alpha: true, powerPreference: "default" }}
      style={{ background: "transparent" }}
    >
      <Scene />
    </Canvas>
  );
}

