"use client";

import { useRef, useMemo, useEffect, useState } from "react";
import { Canvas, useFrame } from "@react-three/fiber";
import { Float, ContactShadows, Line, Environment } from "@react-three/drei";
import * as THREE from "three";

// ── Reduced motion detection ────────────────────────────────────────────────
function useReducedMotion() {
  const [reduced, setReduced] = useState(false);

  useEffect(() => {
    const mq = window.matchMedia("(prefers-reduced-motion: reduce)");
    setReduced(mq.matches);
    const handler = (e: MediaQueryListEvent) => setReduced(e.matches);
    mq.addEventListener("change", handler);
    return () => mq.removeEventListener("change", handler);
  }, []);

  return reduced;
}

// ── Canvas2D page texture generator ─────────────────────────────────────────
function createPageTexture(variant: 0 | 1 | 2): THREE.CanvasTexture {
  const w = 512;
  const h = 720;
  const canvas = document.createElement("canvas");
  canvas.width = w;
  canvas.height = h;
  const ctx = canvas.getContext("2d")!;

  // Paper background — pure white so lighting tints it naturally
  ctx.fillStyle = "#FFFFFF";
  ctx.fillRect(0, 0, w, h);

  // Very subtle warm tint
  ctx.fillStyle = "rgba(253, 248, 240, 0.3)";
  ctx.fillRect(0, 0, w, h);

  // Subtle grain noise
  for (let i = 0; i < 600; i++) {
    const gx = Math.floor(((i * 7919 + 104729) % 100003) / 100003 * w);
    const gy = Math.floor(((i * 6271 + 73856) % 100003) / 100003 * h);
    ctx.fillStyle = `rgba(0,0,0,${0.008 + (i % 3) * 0.004})`;
    ctx.fillRect(gx, gy, 1, 1);
  }

  const margin = 50;
  const contentW = w - margin * 2;

  // Title line (darker for visibility)
  ctx.fillStyle = "#999999";
  const titleWidths = [0.5, 0.45, 0.55];
  roundRect(ctx, margin, 55, contentW * titleWidths[variant], 6, 3);

  // Paragraph blocks
  const paragraphs = [
    [[0.88, 1, 0.72, 0.95], [0.6, 1, 0.78, 0.52], [0.9, 0.68, 1, 0.55]],
    [[0.82, 1, 0.66, 0.95, 0.55], [0.9, 0.74, 1, 0.48, 0.85], [1, 0.6, 0.92, 0.7, 0.44]],
    [[0.92, 1, 0.7, 0.88], [0.58, 1, 0.75, 0.46], [0.85, 0.70, 1, 0.62]],
  ];

  ctx.fillStyle = "#BBBBBB";
  let y = 90;
  for (const block of paragraphs[variant]) {
    for (const lineW of block) {
      roundRect(ctx, margin, y, contentW * lineW, 3.5, 1.75);
      y += 12;
    }
    y += 22;
  }

  const texture = new THREE.CanvasTexture(canvas);
  texture.minFilter = THREE.LinearFilter;
  texture.magFilter = THREE.LinearFilter;
  texture.colorSpace = THREE.SRGBColorSpace;
  return texture;
}

function roundRect(
  ctx: CanvasRenderingContext2D,
  x: number,
  y: number,
  w: number,
  h: number,
  r: number,
) {
  ctx.beginPath();
  ctx.roundRect(x, y, w, h, r);
  ctx.fill();
}

// ── Thesis Page mesh ────────────────────────────────────────────────────────
function ThesisPageMesh({ variant }: { variant: 0 | 1 | 2 }) {
  const texture = useMemo(() => createPageTexture(variant), [variant]);

  useEffect(() => {
    return () => { texture.dispose(); };
  }, [texture]);

  return (
    <mesh castShadow>
      <planeGeometry args={[2.1, 2.97]} />
      <meshStandardMaterial
        map={texture}
        color="#FFFFFF"
        side={THREE.DoubleSide}
        roughness={0.9}
        metalness={0}
        emissive="#FFFFFF"
        emissiveIntensity={0.08}
      />
    </mesh>
  );
}

// ── Thesis pages group — fanned out with real 3D rotation ───────────────────
function ThesisPagesGroup({ reducedMotion }: { reducedMotion: boolean }) {
  const Wrapper = reducedMotion ? StaticGroup : FloatWrapper;

  return (
    <group position={[-1.2, 0.2, 0]}>
      {/* Page A — back-left, strongly rotated away */}
      <Wrapper speed={1.2} floatIntensity={0.3} rotationIntensity={0.08}>
        <group
          rotation={[0.06, -0.45, -0.03]}
          position={[-0.6, 0.15, -0.8]}
        >
          <ThesisPageMesh variant={0} />
        </group>
      </Wrapper>

      {/* Page B — centre-front, slight angle, overlapping */}
      <Wrapper speed={1.0} floatIntensity={0.25} rotationIntensity={0.06}>
        <group
          rotation={[0.04, -0.18, 0.02]}
          position={[0.1, 0.35, 0.2]}
        >
          <ThesisPageMesh variant={1} />
        </group>
      </Wrapper>

