      <Wrapper speed={0.8} floatIntensity={0.2} rotationIntensity={0.07}>
        <group
          rotation={[-0.03, 0.15, 0.04]}
          position={[0.7, -0.1, -0.3]}
        >
          <ThesisPageMesh variant={2} />
        </group>
      </Wrapper>
    </group>
  );
}

