function StudioEnvironment() {
  return (
    <Environment frames={1} resolution={128} background={false}>
      {/* Warm top light */}
      <mesh position={[0, 6, 0]} scale={30}>
        <planeGeometry />
        <meshBasicMaterial color="#FFFEF8" />
      </mesh>
      {/* Cool bottom fill */}
      <mesh position={[0, -6, 0]} scale={30} rotation={[Math.PI, 0, 0]}>
        <planeGeometry />
        <meshBasicMaterial color="#E8E0D0" />
      </mesh>
      {/* Left warm fill */}
      <mesh position={[-8, 0, 0]} scale={20} rotation={[0, Math.PI / 2, 0]}>
        <planeGeometry />
        <meshBasicMaterial color="#FFF5E8" />
      </mesh>
      {/* Right cool fill */}
      <mesh position={[8, 0, 0]} scale={20} rotation={[0, -Math.PI / 2, 0]}>
        <planeGeometry />
        <meshBasicMaterial color="#E8ECF0" />
      </mesh>
      {/* Front highlight */}
      <mesh position={[0, 2, 8]} scale={15} rotation={[0, Math.PI, 0]}>
        <planeGeometry />
        <meshBasicMaterial color="#FFFFFF" />
      </mesh>
    </Environment>
  );
}

// ── Main scene ──────────────────────────────────────────────────────────────
function Scene() {
  const reducedMotion = useReducedMotion();

  return (
    <>
      {/* Procedural env map for glass reflections — no download */}
      <StudioEnvironment />

      {/* Lighting — bright enough for white paper */}
      <ambientLight intensity={1.0} />
      <directionalLight
        position={[5, 8, 5]}
        intensity={1.5}
        castShadow
        shadow-mapSize-width={512}
        shadow-mapSize-height={512}
      />
      <pointLight position={[-3, 3, 2]} intensity={0.4} color="#FFF5E6" />
      {/* Fill from below to soften shadows on pages */}
      <pointLight position={[0, -3, 4]} intensity={0.2} color="#F5F0E8" />

      {/* Ground contact shadows */}
      <ContactShadows
        position={[0, -2.2, 0]}
        opacity={0.35}
        scale={14}
        blur={2.5}
        far={6}
        resolution={256}
      />

      <SceneRotator reducedMotion={reducedMotion}>
        <ThesisPagesGroup reducedMotion={reducedMotion} />
        <ScatterPlotGroup reducedMotion={reducedMotion} />
      </SceneRotator>
    </>
  );
}

// ── Exported component ──────────────────────────────────────────────────────
export function Hero3DScene() {
  return (
    <Canvas
      camera={{ position: [0, 1, 8], fov: 35, near: 0.1, far: 50 }}
      dpr={[1, 1.5]}
      gl={{ antialias: true, alpha: true, powerPreference: "default" }}
      style={{ background: "transparent" }}
    >
      <Scene />
    </Canvas>
  );
}

