  8→// ── Reduced motion detection ────────────────────────────────────────────────
  9→function useReducedMotion() {
 10→  const [reduced, setReduced] = useState(false);
 11→
 12→  useEffect(() => {
 13→    const mq = window.matchMedia("(prefers-reduced-motion: reduce)");
 14→    setReduced(mq.matches);
 15→    const handler = (e: MediaQueryListEvent) => setReduced(e.matches);
 16→    mq.addEventListener("change", handler);
 17→    return () => mq.removeEventListener("change", handler);
 18→  }, []);
 19→
 20→  return reduced;
 21→}
