 23→// ── Canvas2D page texture generator ─────────────────────────────────────────
 24→function createPageTexture(variant: 0 | 1 | 2): THREE.CanvasTexture {
 25→  const w = 512;
 26→  const h = 720;
 27→  const canvas = document.createElement("canvas");
 28→  canvas.width = w;
 29→  canvas.height = h;
 30→  const ctx = canvas.getContext("2d")!;
 31→
 32→  // Paper background — pure white so lighting tints it naturally
 33→  ctx.fillStyle = "#FFFFFF";
 34→  ctx.fillRect(0, 0, w, h);
 35→
 36→  // Very subtle warm tint
 37→  ctx.fillStyle = "rgba(253, 248, 240, 0.3)";
 38→  ctx.fillRect(0, 0, w, h);
 39→
 40→  // Subtle grain noise
 41→  for (let i = 0; i < 600; i++) {
 42→    const gx = Math.floor(((i * 7919 + 104729) % 100003) / 100003 * w);
 43→    const gy = Math.floor(((i * 6271 + 73856) % 100003) / 100003 * h);
 44→    ctx.fillStyle = `rgba(0,0,0,${0.008 + (i % 3) * 0.004})`;
 45→    ctx.fillRect(gx, gy, 1, 1);
 46→  }
 47→
 48→  const margin = 50;
 49→  const contentW = w - margin * 2;
 50→
 51→  // Title line (darker for visibility)
 52→  ctx.fillStyle = "#999999";
 53→  const titleWidths = [0.5, 0.45, 0.55];
 54→  roundRect(ctx, margin, 55, contentW * titleWidths[variant], 6, 3);
 55→
 56→  // Paragraph blocks
 57→  const paragraphs = [
 58→    [[0.88, 1, 0.72, 0.95], [0.6, 1, 0.78, 0.52], [0.9, 0.68, 1, 0.55]],
 59→    [[0.82, 1, 0.66, 0.95, 0.55], [0.9, 0.74, 1, 0.48, 0.85], [1, 0.6, 0.92, 0.7, 0.44]],
 60→    [[0.92, 1, 0.7, 0.88], [0.58, 1, 0.75, 0.46], [0.85, 0.70, 1, 0.62]],
 61→  ];
 62→
 63→  ctx.fillStyle = "#BBBBBB";
 64→  let y = 90;
 65→  for (const block of paragraphs[variant]) {
 66→    for (const lineW of block) {
 67→      roundRect(ctx, margin, y, contentW * lineW, 3.5, 1.75);
 68→      y += 12;
 69→    }
 70→    y += 22;
 71→  }
 72→
 73→  const texture = new THREE.CanvasTexture(canvas);
 74→  texture.minFilter = THREE.LinearFilter;
 75→  texture.magFilter = THREE.LinearFilter;
 76→  texture.colorSpace = THREE.SRGBColorSpace;
 77→  return texture;
 78→}
