 80→function roundRect(
 81→  ctx: CanvasRenderingContext2D,
 82→  x: number,
 83→  y: number,
 84→  w: number,
 85→  h: number,
 86→  r: number,
 87→) {
 88→  ctx.beginPath();
 89→  ctx.roundRect(x, y, w, h, r);
 90→  ctx.fill();
 91→}
