 35→  ctx.fillStyle = "rgba(253, 248, 240, 0.25)";
 36→  ctx.fillRect(0, 0, w, h);
 37→
 38→  // Subtle grain noise
 39→  for (let i = 0; i < 500; i++) {
 40→    const gx = Math.floor(((i * 7919 + 104729) % 100003) / 100003 * w);
 41→    const gy = Math.floor(((i * 6271 + 73856) % 100003) / 100003 * h);
 42→    ctx.fillStyle = `rgba(0,0,0,${0.006 + (i % 3) * 0.003})`;
 43→    ctx.fillRect(gx, gy, 1, 1);
 44→  }
 45→
 46→  const margin = 48;
 47→  const contentW = w - margin * 2;
 48→
 49→  // Title line
 50→  ctx.fillStyle = "#A0A0A0";
 51→  const titleWidths = [0.48, 0.42, 0.52];
 52→  roundRect(ctx, margin, 52, contentW * titleWidths[variant], 5, 2.5);
 53→
 54→  // Paragraph blocks — different per variant
 55→  const paragraphs = [
 56→    [[0.88, 1, 0.72, 0.95], [0.6, 1, 0.78, 0.52], [0.9, 0.68, 1, 0.55]],
 57→    [[0.82, 1, 0.66, 0.95, 0.55], [0.9, 0.74, 1, 0.48, 0.85], [1, 0.6, 0.92, 0.7, 0.44]],
 58→    [[0.92, 1, 0.7, 0.88], [0.58, 1, 0.75, 0.46], [0.85, 0.70, 1, 0.62]],
 59→  ];
 60→
 61→  ctx.fillStyle = "#C0C0C0";
