 91→// ── Thesis Page mesh ────────────────────────────────────────────────────────
 92→function ThesisPageMesh({ variant }: { variant: 0 | 1 | 2 }) {
 93→  const texture = useMemo(() => createPageTexture(variant), [variant]);
 94→
 95→  useEffect(() => {
 96→    return () => { texture.dispose(); };
 97→  }, [texture]);
