 99→  return (
100→    <mesh castShadow>
101→      <planeGeometry args={[2.1, 2.97]} />
102→      <meshStandardMaterial
103→        map={texture}
104→        color="#FFFFFF"
105→        side={THREE.DoubleSide}
106→        roughness={0.92}
107→        metalness={0}
108→        emissive="#FFFFFF"
109→        emissiveIntensity={0.06}
110→      />
111→    </mesh>
112→  );
113→}
