115→// ── Thesis pages group — visible fan, 3 edges showing ───────────────────────
116→function ThesisPagesGroup({ reducedMotion }: { reducedMotion: boolean }) {
117→  const Wrapper = reducedMotion ? StaticGroup : FloatWrapper;
118→
119→  return (
120→    <group position={[-0.6, 0, 0]}>
121→      {/* Page A — back-left, peeks above and left */}
122→      <Wrapper speed={1.1} floatIntensity={0.15} rotationIntensity={0.03}>
123→        <group
124→          rotation={[0.03, -0.08, -0.04]}
125→          position={[-0.35, 0.25, -0.6]}
126→        >
