128→        </group>
129→      </Wrapper>
130→
131→      {/* Page B — front-centre, dominant */}
132→      <Wrapper speed={0.9} floatIntensity={0.12} rotationIntensity={0.02}>
133→        <group
134→          rotation={[0.02, -0.04, 0.01]}
135→          position={[0, 0.05, 0]}
136→        >
137→          <ThesisPageMesh variant={1} />
138→        </group>
139→      </Wrapper>
140→
141→      {/* Page C — back-right, peeks right and slightly down */}
142→      <Wrapper speed={0.7} floatIntensity={0.12} rotationIntensity={0.03}>
143→        <group
144→          rotation={[-0.02, 0.1, 0.04]}
145→          position={[0.4, -0.15, -0.35]}
146→        >
147→          <ThesisPageMesh variant={2} />
148→        </group>
149→      </Wrapper>
150→    </group>
