154→// ── Float / Static wrapper ──────────────────────────────────────────────────
155→function FloatWrapper({
156→  children,
157→  speed,
158→  floatIntensity,
159→  rotationIntensity,
160→}: {
161→  children: React.ReactNode;
162→  speed: number;
163→  floatIntensity: number;
164→  rotationIntensity: number;
