184→// ── Scatter data — 18 points with varying sizes ─────────────────────────────
185→const SCATTER_COLORS = ["#3A7D44", "#E08A3C", "#D4AF37", "#1B5E4B"];
186→
187→// Breathe config per colour group — frequency, phase offset, amplitude
188→const GROUP_BREATHE: Record<string, { freq: number; phase: number; amp: number }> = {
189→  [SCATTER_COLORS[0]]: { freq: 0.7, phase: 0, amp: 0.06 },
190→  [SCATTER_COLORS[1]]: { freq: 1.1, phase: 1.2, amp: 0.08 },
191→  [SCATTER_COLORS[2]]: { freq: 0.9, phase: 2.5, amp: 0.05 },
192→  [SCATTER_COLORS[3]]: { freq: 1.4, phase: 0.8, amp: 0.07 },
193→};
194→
195→const SCATTER_POINTS: {
196→  x: number;
197→  y: number;
198→  z: number;
199→  radius: number;
200→  color: string;
201→}[] = [
202→  // Large spheres
203→  { x: 0.15, y: 0.22, z: 0.30, radius: 0.12, color: SCATTER_COLORS[0] },
204→  { x: 1.65, y: 1.50, z: 0.15, radius: 0.11, color: SCATTER_COLORS[1] },
205→  { x: 0.90, y: 0.85, z: 0.40, radius: 0.10, color: SCATTER_COLORS[2] },
206→  // Medium-large
207→  { x: 0.50, y: 0.52, z: 0.10, radius: 0.09, color: SCATTER_COLORS[3] },
208→  { x: 1.30, y: 1.20, z: 0.35, radius: 0.09, color: SCATTER_COLORS[0] },
209→  { x: 1.10, y: 0.78, z: 0.22, radius: 0.085, color: SCATTER_COLORS[1] },
210→  // Medium
211→  { x: 0.35, y: 0.55, z: 0.45, radius: 0.075, color: SCATTER_COLORS[2] },
212→  { x: 0.75, y: 0.60, z: 0.08, radius: 0.07, color: SCATTER_COLORS[3] },
213→  { x: 1.45, y: 1.35, z: 0.28, radius: 0.07, color: SCATTER_COLORS[0] },
214→  { x: 1.80, y: 1.58, z: 0.38, radius: 0.075, color: SCATTER_COLORS[1] },
215→  // Small
216→  { x: 0.60, y: 0.75, z: 0.50, radius: 0.055, color: SCATTER_COLORS[2] },
217→  { x: 1.20, y: 0.92, z: 0.05, radius: 0.05, color: SCATTER_COLORS[3] },
218→  { x: 0.25, y: 0.10, z: 0.18, radius: 0.05, color: SCATTER_COLORS[0] },
219→  { x: 1.55, y: 1.10, z: 0.48, radius: 0.055, color: SCATTER_COLORS[1] },
220→  // Tiny
221→  { x: 0.45, y: 0.30, z: 0.35, radius: 0.04, color: SCATTER_COLORS[2] },
222→  { x: 0.85, y: 1.05, z: 0.55, radius: 0.04, color: SCATTER_COLORS[3] },
223→  { x: 1.70, y: 1.42, z: 0.10, radius: 0.035, color: SCATTER_COLORS[0] },
224→  { x: 1.00, y: 0.65, z: 0.42, radius: 0.038, color: SCATTER_COLORS[1] },
225→];
