227→// ── Scatter sphere — per-group breathe animation ────────────────────────────
228→function ScatterSphere({
229→  position,
230→  color,
231→  radius,
232→  reducedMotion,
233→}: {
234→  position: [number, number, number];
235→  color: string;
236→  radius: number;
237→  reducedMotion: boolean;
238→}) {
239→  const meshRef = useRef<THREE.Mesh>(null);
240→  const geometry = useMemo(() => new THREE.SphereGeometry(radius, 32, 32), [radius]);
241→  const breathe = GROUP_BREATHE[color] ?? { freq: 0.8, phase: 0, amp: 0.05 };
242→
243→  useFrame(({ clock }) => {
244→    if (reducedMotion || !meshRef.current) return;
245→    const t = clock.getElapsedTime();
246→    const s = 1 + Math.sin(t * breathe.freq + breathe.phase) * breathe.amp;
247→    meshRef.current.scale.setScalar(s);
248→  });
249→
250→  return (
251→    <mesh ref={meshRef} geometry={geometry} position={position} castShadow>
252→      <meshPhysicalMaterial
253→        color={color}
254→        metalness={0.15}
255→        roughness={0.03}
256→        clearcoat={1.0}
257→        clearcoatRoughness={0.02}
258→        transmission={0.2}
259→        thickness={radius * 6}
260→        ior={1.8}
261→        envMapIntensity={2.0}
262→        sheen={0.3}
263→        sheenColor="#FFFFFF"
264→      />
265→    </mesh>
266→  );
267→}
