269→// ── Axes with tick marks — very subtle breathe ──────────────────────────────
270→const AXIS_COLOR = "#C0C0C0";
271→const AXIS_LENGTH = 2.2;
272→const TICK_SIZE = 0.05;
273→const TICK_INTERVAL = 0.5;
274→
275→function ScatterAxes({ reducedMotion }: { reducedMotion: boolean }) {
276→  const groupRef = useRef<THREE.Group>(null);
277→
278→  const ticks = useMemo(() => {
279→    const result: { start: [number, number, number]; end: [number, number, number] }[] = [];
280→
281→    for (let t = TICK_INTERVAL; t <= AXIS_LENGTH; t += TICK_INTERVAL) {
282→      result.push({ start: [t, -TICK_SIZE, 0], end: [t, TICK_SIZE, 0] });
283→    }
284→    for (let t = TICK_INTERVAL; t <= AXIS_LENGTH; t += TICK_INTERVAL) {
285→      result.push({ start: [-TICK_SIZE, t, 0], end: [TICK_SIZE, t, 0] });
286→    }
287→    for (let t = TICK_INTERVAL; t <= 0.6; t += TICK_INTERVAL) {
288→      result.push({ start: [0, -TICK_SIZE, t], end: [0, TICK_SIZE, t] });
289→    }
290→
291→    return result;
292→  }, []);
293→
294→  useFrame(({ clock }) => {
295→    if (reducedMotion || !groupRef.current) return;
296→    const t = clock.getElapsedTime();
297→    const s = 1 + Math.sin(t * 0.35) * 0.008;
298→    groupRef.current.scale.setScalar(s);
299→  });
300→
301→  return (
302→    <group ref={groupRef}>
303→      <Line points={[[0, 0, 0], [AXIS_LENGTH, 0, 0]]} color={AXIS_COLOR} lineWidth={1} />
304→      <Line points={[[0, 0, 0], [0, AXIS_LENGTH, 0]]} color={AXIS_COLOR} lineWidth={1} />
305→      <Line points={[[0, 0, 0], [0, 0, 0.6]]} color={AXIS_COLOR} lineWidth={1} />
306→      {ticks.map((tick, i) => (
307→        <Line key={i} points={[tick.start, tick.end]} color={AXIS_COLOR} lineWidth={1} />
308→      ))}
309→    </group>
310→  );
311→}
