 28→// ── WebGL error boundary ────────────────────────────────────────────────────
 29→interface ErrorBoundaryState {
 30→  hasError: boolean;
 31→}
 32→
 33→class WebGLErrorBoundary extends Component<
 34→  { children: ReactNode },
 35→  ErrorBoundaryState
 36→> {
 37→  constructor(props: { children: ReactNode }) {
 38→    super(props);
 39→    this.state = { hasError: false };
 40→  }
 41→
 42→  static getDerivedStateFromError(): ErrorBoundaryState {
 43→    return { hasError: true };
 44→  }
 45→
 46→  componentDidCatch(error: Error, info: ErrorInfo) {
 47→    console.warn("WebGL error boundary caught:", error, info);
 48→  }
 49→
 50→  render() {
 51→    if (this.state.hasError) {
 52→      return <Hero3DFallback />;
